  });

  // Password breach check endpoint using HaveIBeenPwned API
  app.post("/api/check-password", async (req, res) => {
    try {
      // Validate request body - expect password from frontend
      const { password } = z.object({
        password: z.string().min(1, "Password is required")
      }).parse(req.body);

      // Hash the password using SHA-1 and get the prefix
      const hashedPassword = hashPassword(password);
      const hashPrefix = hashedPassword.substring(0, 5);

      // Query HaveIBeenPwned API with the hash prefix
      const response = await fetch(`https://api.pwnedpasswords.com/range/${hashPrefix}`, {
        method: 'GET',
        headers: {
          'User-Agent': 'Breach-Buster-Security-App',
        }
      });

      if (!response.ok) {
        throw new Error(`HaveIBeenPwned API responded with status: ${response.status}`);
      }

      const data = await response.text();

      // Get the full hash suffix to compare
      const hashSuffix = hashedPassword.substring(5).toLowerCase();

      // Parse the response and look for our specific hash
      const hashSuffixes = data.split('\n').map(line => {
        const [suffix, count] = line.trim().split(':');
        return { suffix: suffix.toLowerCase(), count: parseInt(count, 10) };
      }).filter(item => item.suffix && !isNaN(item.count));

      // Check if our password hash is in the breached list
      const match = hashSuffixes.find(item => item.suffix === hashSuffix);

      if (match) {
        // Password was found in breaches
        res.json({
          success: true,
          isBreached: true,
          count: match.count,
          message: `This password has been found in ${match.count.toLocaleString()} data breaches.`
        });
      } else {
        // Password not found in breaches
        res.json({
          success: true,
          isBreached: false,
          count: 0,
          message: "This password has not been found in any known data breaches."
        });
      }

    } catch (error: any) {
      console.error("Password check error:", error);

      if (error instanceof z.ZodError) {
        return res.status(400).json({
          success: false,
          message: "Invalid request format",
          errors: error.errors.map(err => err.message)
        });
      }

      res.status(500).json({
        success: false,
        message: "Failed to check password. Please try again later."
      });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { users, type User, type InsertUser, copyrightClaims, type CopyrightClaim, type InsertCopyrightClaim } from "@shared/schema";

// modify the interface with any CRUD methods
// you might need

export interface IStorage {
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  createCopyrightClaim(claim: InsertCopyrightClaim, evidenceFilePath: string, evidenceFileName: string): Promise<CopyrightClaim>;
  getCopyrightClaims(): Promise<CopyrightClaim[]>;
  getCopyrightClaim(id: number): Promise<CopyrightClaim | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<number, User>;
  private copyrightClaims: Map<number, CopyrightClaim>;
  currentUserId: number;
  currentClaimId: number;

  constructor() {
    this.users = new Map();
    this.copyrightClaims = new Map();
    this.currentUserId = 1;
    this.currentClaimId = 1;
  }

  async getUser(id: number): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = this.currentUserId++;
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createCopyrightClaim(insertClaim: InsertCopyrightClaim, evidenceFilePath: string, evidenceFileName: string): Promise<CopyrightClaim> {
    const id = this.currentClaimId++;
    const claim: CopyrightClaim = {
      ...insertClaim,
      id,
      submittedAt: new Date(),
      status: "pending",
      contactDetails: insertClaim.contactDetails || null,
      evidenceFilePath,
      evidenceFileName
    };
    this.copyrightClaims.set(id, claim);
    return claim;
  }

  async getCopyrightClaims(): Promise<CopyrightClaim[]> {
    return Array.from(this.copyrightClaims.values());
  }

  async getCopyrightClaim(id: number): Promise<CopyrightClaim | undefined> {
    return this.copyrightClaims.get(id);
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { pgTable, text, serial, integer, boolean, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;

// Copyright Claim Table
export const copyrightClaims = pgTable("copyright_claims", {
  id: serial("id").primaryKey(),
  claimantName: text("claimant_name").notNull(),
  claimantEmail: text("claimant_email").notNull(),
  copyrightedWork: text("copyrighted_work").notNull(),
  infringingUrl: text("infringing_url").notNull(),
  description: text("description").notNull(),
  contactDetails: text("contact_details"),
  evidenceFilePath: text("evidence_file_path").notNull(),
  evidenceFileName: text("evidence_file_name").notNull(),
  submittedAt: timestamp("submitted_at").defaultNow().notNull(),
  status: text("status").default("pending").notNull(), // pending, reviewing, resolved, rejected
});

export const insertCopyrightClaimSchema = createInsertSchema(copyrightClaims).omit({
  id: true,
  submittedAt: true,
  status: true,
  evidenceFilePath: true,
  evidenceFileName: true,
}).extend({
  claimantName: z.string().min(1, "Name is required"),
  claimantEmail: z.string().email("Please enter a valid email address"),
  copyrightedWork: z.string().min(1, "Please describe your copyrighted work"),
  infringingUrl: z.string().url("Please enter a valid URL"),
  description: z.string().min(10, "Please provide a detailed description (minimum 10 characters)"),
  contactDetails: z.string().optional(),
});

export type InsertCopyrightClaim = z.infer<typeof insertCopyrightClaimSchema>;
export type CopyrightClaim = typeof copyrightClaims.$inferSelect;

