/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Ubuntu', sans-serif;
    background-color: #000;
    color: #fff;
    overflow-x: hidden;
    line-height: 1.6;
}

/* Background Animations */
.background-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
}

.hero-gradient {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(
        ellipse at center,
        rgba(52, 41, 211, 0.3) 0%,
        rgba(27, 21, 109, 0.1) 50%,
        transparent 100%
    );
    animation: breathe 20s ease-in-out infinite;
}

.floating-orb {
    position: absolute;
    border-radius: 50%;
    filter: blur(40px);
}

.orb-1 {
    top: 25%;
    left: 25%;
    width: 8rem;
    height: 8rem;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.3), rgba(147, 51, 234, 0.3));
    animation: float-1 8s ease-in-out infinite;
}

.orb-2 {
    top: 75%;
    right: 25%;
    width: 6rem;
    height: 6rem;
    background: linear-gradient(135deg, rgba(147, 51, 234, 0.2), rgba(59, 130, 246, 0.2));
    animation: float-2 10s ease-in-out infinite 2s;
}

/* Content Wrapper */
.content-wrapper {
    position: relative;
    z-index: 10;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

/* Header Styles */
.header {
    width: 100%;
    padding: 1.5rem 1rem;
    animation: slideInDown 0.8s ease-out;
}

.header-container {
    max-width: 1280px;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.logo-container {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    cursor: pointer;
    transition: transform 0.3s ease;
    animation: fadeInLeft 0.8s ease-out 0.2s both;
}

.logo-container:hover {
    transform: scale(1.05);
}

.logo-b {
