    font-weight: normal;
    color: #fff;
}

.logo-text span {
    font-size: 1.25rem;
    line-height: 1.1;
}

/* Navigation Styles */
.desktop-nav {
    display: none;
    animation: fadeInUp 0.8s ease-out 0.4s both;
}

.nav-container {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12.5px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 50px;
    padding: 0.75rem 1.5rem;
}

.nav-list {
    display: flex;
    align-items: center;
    gap: 2rem;
    list-style: none;
}

.nav-link {
    font-family: 'Ubuntu', sans-serif;
    font-weight: bold;
    font-size: 1rem;
    color: rgba(255, 255, 255, 0.7);
    text-decoration: none;
    transition: all 0.3s ease;
    cursor: pointer;
    padding: 0.5rem 1rem;
    border-radius: 20px;
}

.nav-link:hover,
.nav-link.active {
    color: #fff;
    transform: scale(1.1);
}

.nav-link.active {
    background: rgba(255, 255, 255, 0.1);
}

/* Settings Dropdown */
.settings-container {
    position: relative;
    z-index: 10000;
}

.settings-toggle {
    width: 3rem;
    height: 3rem;
    background: rgba(255, 255, 255, 0.1);
    border: none;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    animation: fadeInRight 0.8s ease-out 0.6s both;
    position: relative;
}

.settings-toggle:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: scale(1.1);
}

.settings-icon {
    width: 1.5rem;
    height: 1.5rem;
    color: #fff;
    transition: all 0.3s ease;
    animation: settingsRotate 8s linear infinite;
}

.settings-toggle:hover .settings-icon {
    animation-duration: 2s;
}

.settings-dropdown {
    position: absolute;
    top: calc(100% + 0.5rem);
    right: 0;
    width: 180px;
    background: rgba(15, 23, 42, 0.95);
    border: 1px solid rgba(0, 255, 255, 0.2);
    border-radius: 8px;
    backdrop-filter: blur(15px);
    box-shadow: 
        0 8px 25px rgba(0, 0, 0, 0.6),
        0 0 15px rgba(0, 255, 255, 0.1);
    opacity: 0;
    visibility: hidden;
    transform: translateY(-10px) scale(0.95);
    transition: all 0.2s ease;
    z-index: 10001;
    overflow: hidden;
}

.settings-dropdown::before {
    content: '';
    position: absolute;
    top: -5px;
    right: 12px;
    width: 10px;
    height: 10px;
    background: rgba(15, 23, 42, 0.95);
    border-top: 1px solid rgba(0, 255, 255, 0.2);
    border-left: 1px solid rgba(0, 255, 255, 0.2);
    transform: rotate(45deg);
}

.settings-container:hover .settings-dropdown,
.settings-dropdown:hover {
    opacity: 1;
    visibility: visible;
    transform: translateY(0) scale(1);
}

.dropdown-content {
    padding: 12px;
    box-sizing: border-box;
    width: 100%;
}

.setting-item {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 6px 0;
    transition: all 0.2s ease;
    box-sizing: border-box;
    width: 100%;
}

.setting-item:not(:last-child) {
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    margin-bottom: 0.3rem;
    padding-bottom: 0.4rem;
}

.setting-info {
    display: flex;
    align-items: center;
    gap: 8px;
    flex: 1;
    min-width: 0;
    max-width: calc(100% - 70px);
}

.setting-icon {
    font-size: 0.9rem;
    flex-shrink: 0;
}

.setting-name {
    font-family: 'Ubuntu', sans-serif;
    color: #ffffff;
    font-size: 0.75rem;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.modern-toggle {
    position: relative;
    flex-shrink: 0;
    width: 60px;
    height: 24px;
}

.toggle-input {
    display: none;
}

.toggle-label {
    display: block;
    cursor: pointer;
    width: 60px;
    height: 24px;
}

.toggle-track {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 60px;
    height: 24px;
    background: linear-gradient(135deg, #ef4444, #dc2626);
    border: 2px solid rgba(239, 68, 68, 0.3);
    border-radius: 12px;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    overflow: hidden;
    box-shadow: 
        inset 0 2px 4px rgba(0, 0, 0, 0.3),
        0 0 8px rgba(239, 68, 68, 0.2);
    font-family: 'Ubuntu', sans-serif;
    font-size: 8px;
    font-weight: bold;
    color: white;
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.8);
}

.toggle-track::before {
    content: 'OFF';
    position: absolute;
    left: 6px;
    z-index: 1;
    opacity: 1;
    transition: opacity 0.3s ease;
}

.toggle-track::after {
    content: 'ON';
    position: absolute;
    right: 8px;
    z-index: 1;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.toggle-thumb {
    position: absolute;
    top: 1px;
    left: 1px;
    width: 20px;
    height: 20px;
    background: linear-gradient(135deg, #ffffff, #f1f5f9);
    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 50%;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 
        0 2px 6px rgba(0, 0, 0, 0.3),
        0 0 8px rgba(255, 255, 255, 0.2);
    cursor: pointer;
    z-index: 2;
}

.toggle-thumb::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 8px;
    height: 8px;
    background: radial-gradient(circle, rgba(100, 116, 139, 0.2), transparent);
    border-radius: 50%;
    transition: all 0.3s ease;
}

.toggle-input:checked + .toggle-label .toggle-track {
    background: linear-gradient(135deg, #10b981, #059669);
    border-color: rgba(16, 185, 129, 0.4);
    box-shadow: 
        inset 0 2px 4px rgba(0, 0, 0, 0.2),
        0 0 12px rgba(16, 185, 129, 0.3),
        0 0 20px rgba(16, 185, 129, 0.1);
}

.toggle-input:checked + .toggle-label .toggle-track::before {
    opacity: 0;
}

.toggle-input:checked + .toggle-label .toggle-track::after {
    opacity: 1;
}

.toggle-input:checked + .toggle-label .toggle-thumb {
    transform: translateX(36px);
    background: linear-gradient(135deg, #ffffff, #ecfdf5);
    box-shadow: 
        0 2px 8px rgba(0, 0, 0, 0.4),
        0 0 12px rgba(16, 185, 129, 0.2);
}

.toggle-input:checked + .toggle-label .toggle-thumb::before {
    background: radial-gradient(circle, rgba(16, 185, 129, 0.3), transparent);
}

.toggle-label:hover .toggle-track {
    transform: scale(1.05);
    box-shadow: 
        inset 0 2px 4px rgba(0, 0, 0, 0.3),
        0 0 12px rgba(239, 68, 68, 0.3);
}

.toggle-input:checked + .toggle-label:hover .toggle-track {
    box-shadow: 
        inset 0 2px 4px rgba(0, 0, 0, 0.2),
        0 0 15px rgba(16, 185, 129, 0.4);
}

.toggle-label:hover .toggle-thumb {
    box-shadow: 
        0 3px 10px rgba(0, 0, 0, 0.4),
        0 0 15px rgba(255, 255, 255, 0.3);
}

.toggle-input:checked + .toggle-label:hover .toggle-thumb {
    box-shadow: 
        0 3px 10px rgba(0, 0, 0, 0.5),
        0 0 15px rgba(16, 185, 129, 0.4);
}


/* Animations */
@keyframes settingsRotate {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}


/* Animations Disabled State */
.animations-disabled * {
    animation-duration: 0s !important;
    animation-delay: 0s !important;
    transition-duration: 0.1s !important;
}

.animations-disabled .floating-orb,
.animations-disabled .particle,
.animations-disabled .loading-particle,
.animations-disabled .cyber-particle,
.animations-disabled .neural-node,
.animations-disabled .stream,
.animations-disabled .pulse {
    animation: none !important;
}

.animations-disabled .settings-icon {
    animation: none !important;
}

.animations-disabled .hero-gradient {
    animation: none !important;
}

/* Keep essential UI transitions even when animations are disabled */
.animations-disabled .settings-dropdown,
.animations-disabled .toggle-track,
.animations-disabled .toggle-thumb {
    transition-duration: 0.2s !important;
}

/* Main Content */
.main-content {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
