/* Mobile navigation styles removed - now handled by React component */

/* Responsive Design */
/* Mobile-first approach for Android optimization */
@media (max-width: 639px) {
    .header {
        padding: 1rem 1rem;
        min-height: 70px;
    }

    .header-container {
        gap: 0.5rem;
    }

    .logo-b {
        font-size: 2.5rem;
    }

    .logo-text span {
        font-size: 1rem;
        line-height: 1.2;
    }

    .settings-container {
        flex-shrink: 0;
    }

    .settings-toggle {
        width: 44px;
        height: 44px;
        min-width: 44px;
        min-height: 44px;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    /* Mobile navigation styles removed - handled by React component */

    /* Hide desktop nav on mobile */
    .desktop-nav {
        display: none !important;
    }

    /* Main content padding adjustment for mobile nav */
    .main-content {
        padding-bottom: 80px;
    }

    /* Improved touch targets */
    .nav-link, .mobile-nav-item, .settings-toggle {
        -webkit-tap-highlight-color: rgba(255, 255, 255, 0.1);
        touch-action: manipulation;
    }

    /* Fix overflow and scrolling issues on Android */
    body {
        -webkit-overflow-scrolling: touch;
        overflow-x: hidden;
    }

    .content-wrapper {
        min-height: 100vh;
        min-height: 100dvh; /* Dynamic viewport height for mobile browsers */
    }
}

@media (min-width: 640px) {
    .header {
        padding: 2rem 1.5rem;
    }

    .main-content {
        padding-bottom: 0;
    }

    .logo-b {
        font-size: 3.5rem;
    }

    .logo-text span {
