    backdrop-filter: blur(20px);
}

.enhanced-password-generator::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg,
        transparent 30%,
        rgba(0, 255, 255, 0.05) 50%,
        transparent 70%
    );
