    animation: borderGlow 4s ease-in-out infinite;
    z-index: 1;
}

/* Cyber Atmosphere Background Effects */
.cyber-atmosphere {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    overflow: hidden;
    z-index: 1;
}

.neural-network {
    position: absolute;
    width: 100%;
    height: 100%;
    opacity: 0.3;
}

.neural-node {
    position: absolute;
    width: 6px;
    height: 6px;
    background: rgba(0, 255, 255, 0.8);
    border-radius: 50%;
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.6);
    animation: nodePulse 3s ease-in-out infinite;
}

.node-1 { top: 20%; left: 15%; animation-delay: 0s; }
.node-2 { top: 30%; right: 20%; animation-delay: 0.5s; }
.node-3 { bottom: 25%; left: 25%; animation-delay: 1s; }
.node-4 { bottom: 35%; right: 15%; animation-delay: 1.5s; }

.neural-connection {
    position: absolute;
    width: 2px;
    height: 200px;
    background: linear-gradient(45deg, 
        rgba(0, 255, 255, 0.2), 
        transparent, 
        rgba(0, 255, 255, 0.3)
    );
    transform: rotate(45deg);
    top: 40%;
    left: 50%;
    animation: connectionFlow 4s linear infinite;
}

.data-streams {
    position: absolute;
    width: 100%;
    height: 100%;
}

.stream {
    position: absolute;
    width: 1px;
    height: 100px;
    background: linear-gradient(to bottom,
        transparent,
        rgba(0, 255, 255, 0.6),
        transparent
    );
    animation: streamFlow 6s linear infinite;
}

.stream-1 { left: 10%; animation-delay: 0s; }
.stream-2 { left: 50%; animation-delay: 2s; }
.stream-3 { right: 10%; animation-delay: 4s; }

.holographic-grid {
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: 
        linear-gradient(rgba(0, 255, 255, 0.03) 1px, transparent 1px),
        linear-gradient(90deg, rgba(0, 255, 255, 0.03) 1px, transparent 1px),
        radial-gradient(circle at 25% 25%, rgba(0, 255, 255, 0.1) 1px, transparent 1px);
    background-size: 100px 100px, 100px 100px, 50px 50px;
    animation: gridShift 20s linear infinite;
    opacity: 0.4;
}

.energy-pulses {
    position: absolute;
    width: 100%;
    height: 100%;
}

.pulse {
    position: absolute;
    width: 8px;
    height: 8px;
    background: rgba(0, 255, 255, 0.8);
    border-radius: 50%;
    box-shadow: 0 0 20px rgba(0, 255, 255, 0.8);
    animation: energyPulse 4s ease-in-out infinite;
}

.pulse-1 { top: 15%; left: 80%; animation-delay: 0s; }
.pulse-2 { bottom: 20%; left: 10%; animation-delay: 1.3s; }
.pulse-3 { top: 60%; right: 30%; animation-delay: 2.6s; }

