.section-title1 {
  font-size: 2rem !important;   /* force larger font */
  font-family: 'Orbitron', sans-serif;
  font-weight: 800;   /* ⬅️ makes it extra bold */
  color: #fff;
  text-align: center;
  text-transform: uppercase;
  position: relative;
  display: block;
  margin: 20px auto;   /* keeps it centered */
  width: fit-content;
  animation: flicker 3s infinite alternate;
  text-shadow:
    0 0 6px #0ff,
    0 0 12px #0ff,
    0 0 24px #0ff,
    0 0 48px #0ff;
}



/* Neon flicker animation */
@keyframes flicker {
  0%, 18%, 22%, 25%, 53%, 57%, 100% {
    text-shadow:
      0 0 5px #0ff,
      0 0 10px #0ff,
      0 0 20px #0ff,
      0 0 40px #0ff,
      0 0 80px #0ff;
    color: #fff;
  }
  20%, 24%, 55% {
    text-shadow: none;
    color: #0ff;
  }
}

/* Glitch effect */
.section-title1::before,
.section-title1::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  text-align: center;
  overflow: hidden;
}

.section-title1::before {
  color: #0ff;
  clip-path: inset(0 0 50% 0);
  animation: glitch 2s infinite linear alternate-reverse;
}

.section-title1::after {
  color: #f0f;
  clip-path: inset(50% 0 0 0);
  animation: glitch 3s infinite linear alternate-reverse;
}

@keyframes glitch {
  0% { clip-path: inset(80% 0 0 0); }
  20% { clip-path: inset(10% 0 85% 0); }
  40% { clip-path: inset(40% 0 43% 0); }
  60% { clip-path: inset(50% 0 20% 0); }
  80% { clip-path: inset(80% 0 5% 0); }
  100% { clip-path: inset(10% 0 85% 0); }
}



