.timeline-item:nth-child(odd) {
    flex-direction: row-reverse;
}

.timeline-marker {
    position: relative;
    width: 60px;
    height: 60px;
    background: rgba(0, 0, 0, 0.8);
    border: 2px solid #00ffff;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    z-index: 2;
    margin: 0 2rem;
}

.marker-glow {
    position: absolute;
    width: 80px;
    height: 80px;
    border-radius: 50%;
    animation: markerGlow 2s infinite;
}

.timeline-content {
    flex: 1;
    padding: 1.5rem;
    background: rgba(0, 0, 0, 0.6);
    border: 1px solid rgba(0, 255, 255, 0.2);
    border-radius: 15px;
    backdrop-filter: blur(10px);
}

.timeline-content h4 {
    color: #00ffff;
    margin-bottom: 0.5rem;
    font-size: 1.2rem;
    text-align: center;
}

/* Certifications */
.certifications-section {
    margin: 4rem 0;
    position: relative;
    z-index: 10;
}

.cert-badge {
    font-size: 3rem;
    animation: bounce 2s infinite;
}

.timeline-icon {
    font-size: 3rem;
    animation: bounce 2s infinite;
}

.certifications-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    max-width: none;
    margin: 0 auto;
}

.cert-item {
    text-align: center;
    padding: 2rem;
    background: rgba(0, 0, 0, 0.5);
    border: 1px solid rgba(0, 255, 255, 0.2);
    border-radius: 15px;
    transition: all 0.3s ease;
    position: relative;
    z-index: 10;
}

.cert-item:hover {
    transform: translateY(-5px);
    border-color: rgba(0, 255, 255, 0.5);
}

.cert-icon {
    font-size: 2.5rem;
    margin-bottom: 1rem;
}

/* Floating Action Button */
.floating-action-btn {
    position: fixed;
    bottom: 30px;
    right: 30px;
    z-index: 100;
    animation: float 3s ease-in-out infinite;
}

.fab-content {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 1rem 1.5rem;
    background: linear-gradient(45deg, #00ffff, #ff00ff);
    border-radius: 30px;
    color: #000;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
}

.fab-content:hover {
    transform: scale(1.1);
    box-shadow: 0 10px 30px rgba(0, 255, 255, 0.5);
}

/* Keyframes */
@keyframes glitch-1 {
    0%, 100% { 
        transform: translate(0, 0);
        clip-path: polygon(0 0, 100% 0, 100% 45%, 0 45%);
    }
    20% { 
        transform: translate(-2px, 2px);
        clip-path: polygon(0 0, 100% 0, 100% 35%, 0 35%);
    }
    40% { 
        transform: translate(-2px, -2px);
        clip-path: polygon(0 0, 100% 0, 100% 40%, 0 40%);
    }
    60% { 
        transform: translate(2px, 2px);
        clip-path: polygon(0 0, 100% 0, 100% 30%, 0 30%);
    }
    80% { 
        transform: translate(2px, -2px);
        clip-path: polygon(0 0, 100% 0, 100% 50%, 0 50%);
    }
}

@keyframes glitch-2 {
    0%, 100% { 
        transform: translate(0, 0);
        clip-path: polygon(0 55%, 100% 55%, 100% 100%, 0 100%);
    }
    20% { 
        transform: translate(2px, -2px);
        clip-path: polygon(0 60%, 100% 60%, 100% 100%, 0 100%);
    }
    40% { 
        transform: translate(2px, 2px);
        clip-path: polygon(0 50%, 100% 50%, 100% 100%, 0 100%);
    }
    60% { 
        transform: translate(-2px, -2px);
        clip-path: polygon(0 65%, 100% 65%, 100% 100%, 0 100%);
    }
    80% { 
        transform: translate(-2px, 2px);
        clip-path: polygon(0 45%, 100% 45%, 100% 100%, 0 100%);
    }
}

@keyframes blink {
    0%, 50% { opacity: 1; }
    51%, 100% { opacity: 0; }
}

@keyframes typeWriter {
    from { width: 0; }
    to { width: 100%; }
}

@keyframes pulse {
    0%, 100% { opacity: 1; transform: scale(1); }
    50% { opacity: 0.5; transform: scale(1.1); }
}

@keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

@keyframes rotateCube {
    0% { transform: rotateX(0deg) rotateY(0deg); }
    100% { transform: rotateX(360deg) rotateY(360deg); }
}

@keyframes techGlow {
    0%, 100% { opacity: 0.5; transform: translate(-50%, -50%) scale(1); }
    50% { opacity: 1; transform: translate(-50%, -50%) scale(1.2); }
}

@keyframes fillMeter {
    from { width: 0%; }
    to { width: var(--fill, 100%); }
}

@keyframes ringPulse {
    0%, 100% { transform: translate(-50%, -50%) scale(1); opacity: 1; }
    50% { transform: translate(-50%, -50%) scale(1.2); opacity: 0.5; }
}

@keyframes skillFill {
    from { width: 0%; }
    to { width: var(--skill, 100%); }
}

@keyframes iconPulse {
    0%, 100% { opacity: 0.6; }
    50% { opacity: 1; }
}

@keyframes progressFill {
    from { width: 0%; }
    to { width: var(--progress, 100%); }
}

@keyframes markerGlow {
    0%, 100% { box-shadow: 0 0 10px rgba(0, 255, 255, 0.3); }
    50% { box-shadow: 0 0 30px rgba(0, 255, 255, 0.8); }
}

@keyframes bounce {
    0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
    40% { transform: translateY(-10px); }
    60% { transform: translateY(-5px); }
}

@keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
}

@keyframes aboutParticleFloat {
    0%, 100% { 
        transform: translateY(0px) translateX(0px);
        opacity: 0.7;
    }
    25% { 
        transform: translateY(-20px) translateX(10px);
        opacity: 1;
    }
    50% { 
        transform: translateY(-10px) translateX(-5px);
        opacity: 0.5;
    }
    75% { 
        transform: translateY(-30px) translateX(15px);
        opacity: 0.8;
    }
}

/* Responsive Design */
@media (max-width: 768px) {
    .generator-content {
        padding: 2rem 1rem;
    }

    .generator-title {
        font-size: 2rem;
    }

    .password-actions {
        flex-direction: column;
    }

    .security-tips {
        flex-direction: column;
        align-items: center;
    }

    .options-grid {
        grid-template-columns: 1fr;
    }

    .glitch-text {
        font-size: 2.5rem;
    }

    .status-grid {
        flex-direction: column;
        align-items: center;
    }

    .mission-stats {
        flex-direction: column;
        align-items: center;
    }

    .tech-grid {
        grid-template-columns: 1fr;
    }

    .features-grid-enhanced {
        grid-template-columns: 1fr;
    }

    .timeline-line {
        left: 30px;
    }

    .timeline-item {
