    }
}

/* Cyber Password Generator CSS */
.cyber-password-generator-section {
    padding: 4rem 2rem;
    margin: 4rem 0;
    background: linear-gradient(135deg, rgba(0, 255, 255, 0.03) 0%, rgba(0, 0, 255, 0.02) 50%, rgba(128, 0, 255, 0.03) 100%);
    border: 1px solid rgba(0, 255, 255, 0.1);
    border-radius: 20px;
    position: relative;
    overflow: hidden;
    backdrop-filter: blur(10px);
}

.password-generator-content {
    max-width: 1000px;
    margin: 0 auto;
    position: relative;
    z-index: 10;
}

.cyber-header {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 1rem;
    margin-bottom: 2rem;
    position: relative;
}

.generator-heading {
    font-family: 'Russo One', sans-serif;
    font-size: 2.5rem;
    color: #00ffff;
    text-transform: uppercase;
    letter-spacing: 2px;
    text-shadow: 0 0 20px rgba(0, 255, 255, 0.5);
    animation: cyberGlow 2s ease-in-out infinite alternate;
}

.cyber-line {
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 200px;
    height: 2px;
    background: linear-gradient(90deg, transparent, #00ffff, transparent);
    animation: lineGlow 3s ease-in-out infinite;
}

.generator-description {
    text-align: center;
    font-size: 1.1rem;
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 3rem;
    line-height: 1.6;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
}

.password-display-container {
    margin-bottom: 3rem;
}

.password-display {
    background: rgba(0, 0, 0, 0.6);
    border: 2px solid rgba(0, 255, 255, 0.3);
    border-radius: 15px;
    padding: 2rem;
    position: relative;
    overflow: hidden;
}

.password-display::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 255, 255, 0.1), transparent);
    animation: scanLine 4s infinite linear;
}

.password-output {
    background: rgba(0, 0, 0, 0.8);
    border: 1px solid rgba(0, 255, 255, 0.2);
    border-radius: 10px;
    padding: 1.5rem;
    margin-bottom: 1.5rem;
    min-height: 80px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-family: 'Courier New', monospace;
    position: relative;
    overflow: hidden;
}

.password-text {
    font-size: 1.4rem;
    color: #00ff88;
    letter-spacing: 2px;
    text-shadow: 0 0 10px rgba(0, 255, 136, 0.5);
    font-weight: 600;
    user-select: all;
}

.generating-animation {
    text-align: center;
    width: 100%;
}

.generating-text {
    display: block;
    color: #ff6b35;
    font-size: 1.2rem;
    font-weight: bold;
    margin-bottom: 1rem;
    animation: pulse 1.5s infinite;
}

.cyber-loader {
    font-family: 'Courier New', monospace;
    font-size: 1.2rem;
    letter-spacing: 2px;
    color: #00ffff;
    text-shadow: 0 0 5px rgba(0, 255, 255, 0.8);
    line-height: 1.4;
    word-break: break-all;
}

.loader-char {
    display: inline-block;
}

.password-actions {
    display: flex;
    gap: 1rem;
    justify-content: center;
}

.action-btn {
    background: rgba(0, 255, 255, 0.1);
    border: 2px solid rgba(0, 255, 255, 0.3);
    color: #00ffff;
    padding: 0.8rem 1.5rem;
    border-radius: 10px;
    font-family: 'Russo One', sans-serif;
    font-size: 0.9rem;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    text-transform: uppercase;
    letter-spacing: 1px;
    position: relative;
    overflow: hidden;
}

.action-btn:hover {
    background: rgba(0, 255, 255, 0.2);
    border-color: rgba(0, 255, 255, 0.6);
    box-shadow: 0 0 20px rgba(0, 255, 255, 0.3);
    transform: translateY(-2px);
}

.action-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.action-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(0, 255, 255, 0.2), transparent);
    transition: left 0.5s;
}

.action-btn:hover::before {
    left: 100%;
}

.copy-btn.copied {
    background: rgba(0, 255, 0, 0.1);
    border-color: rgba(0, 255, 0, 0.3);
    color: #00ff00;
}

.btn-icon {
    transition: all 0.3s ease;
}

.generator-logo-b {
    font-family: 'Rubik Mono One', monospace;
    font-size: 2.5rem;
    color: #00ffff;
    transform: rotate(180deg);
    animation: logoSpin 3s ease-in-out infinite;
    text-shadow: 0 0 20px rgba(0, 255, 255, 0.8);
    filter: drop-shadow(0 0 10px rgba(0, 255, 255, 0.5));
}

.password-controls {
    margin-bottom: 3rem;
}

.controls-grid {
    display: grid;
    gap: 2rem;
    max-width: 800px;
    margin: 0 auto;
}

.control-group {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(0, 255, 255, 0.2);
    border-radius: 12px;
    padding: 1.5rem;
}

.control-label {
    display: block;
    font-size: 1.1rem;
    color: #00ffff;
    margin-bottom: 1rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.length-value {
    color: #ff6b35;
    font-weight: bold;
    text-shadow: 0 0 5px rgba(255, 107, 53, 0.5);
}

.cyber-slider {
    width: 100%;
    height: 8px;
    background: rgba(0, 0, 0, 0.6);
    border-radius: 4px;
    outline: none;
    border: 1px solid rgba(0, 255, 255, 0.3);
    -webkit-appearance: none;
}

.cyber-slider::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 20px;
    height: 20px;
    background: linear-gradient(135deg, #00ffff, #0088cc);
    border-radius: 50%;
    cursor: pointer;
    box-shadow: 0 0 10px rgba(0, 255, 255, 0.5);
    border: 2px solid #00ffff;
}

.cyber-slider::-webkit-slider-thumb:hover {
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.8);
    transform: scale(1.1);
}

.checkbox-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
}

.cyber-checkbox {
    display: flex;
    align-items: center;
    gap: 0.8rem;
    cursor: pointer;
    padding: 0.8rem;
    border-radius: 8px;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgba(0, 255, 255, 0.2);
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.cyber-checkbox:hover {
    background: rgba(0, 255, 255, 0.05);
    border-color: rgba(0, 255, 255, 0.4);
}

.cyber-checkbox input[type="checkbox"] {
    display: none;
}

.checkmark {
    width: 20px;
    height: 20px;
    border: 2px solid rgba(0, 255, 255, 0.5);
    border-radius: 4px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    background: rgba(0, 0, 0, 0.6);
    transition: all 0.3s ease;
}

.cyber-checkbox input[type="checkbox"]:checked + .checkmark {
    background: rgba(0, 255, 255, 0.2);
    border-color: #00ffff;
    box-shadow: 0 0 10px rgba(0, 255, 255, 0.3);
}

.cyber-checkbox input[type="checkbox"]:checked + .checkmark::after {
    content: '✓';
    color: #00ffff;
    font-size: 14px;
    font-weight: bold;
    text-shadow: 0 0 5px rgba(0, 255, 255, 0.8);
    animation: checkPop 0.3s ease;
}

.checkbox-label {
    color: rgba(255, 255, 255, 0.8);
    font-size: 0.95rem;
    font-weight: 500;
}

.security-info {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(0, 255, 255, 0.2);
    border-radius: 15px;
    padding: 2rem;
}

.info-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
}

.info-item {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem;
    background: rgba(0, 0, 0, 0.3);
    border: 1px solid rgba(0, 255, 255, 0.1);
    border-radius: 10px;
    transition: all 0.3s ease;
}

.info-item:hover {
    background: rgba(0, 255, 255, 0.05);
    border-color: rgba(0, 255, 255, 0.3);
    transform: translateY(-2px);
}

.info-icon {
    font-size: 1.5rem;
    filter: drop-shadow(0 0 5px rgba(255, 255, 255, 0.3));
}

.info-text {
    display: flex;
    flex-direction: column;
    gap: 0.3rem;
}

.info-title {
    color: #00ffff;
    font-weight: 600;
    font-size: 0.9rem;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.info-desc {
    color: rgba(255, 255, 255, 0.7);
    font-size: 0.85rem;
    line-height: 1.4;
}

/* Cyber Background Effects */
.cyber-atmosphere {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
