}

.cyber-scanner-background::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, 
        transparent 40%, 
        rgba(0, 255, 255, 0.01) 50%, 
        transparent 60%
    );
    animation: scannerSweep 12s linear infinite;
}

@keyframes scannerSweep {
    0% {
        transform: translateX(-100%) translateY(-100%) rotate(45deg);
    }
    100% {
        transform: translateX(100vw) translateY(100vh) rotate(45deg);
    }
}

.scanning-grid {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        linear-gradient(rgba(59, 130, 246, 0.03) 1px, transparent 1px),
        linear-gradient(90deg, rgba(59, 130, 246, 0.03) 1px, transparent 1px);
    background-size: 50px 50px;
    animation: gridPulse 8s ease-in-out infinite;
    opacity: 0.4;
}

.data-streams {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

.stream {
    position: absolute;
    width: 2px;
    height: 100px;
    background: linear-gradient(to bottom, transparent, rgba(0, 255, 255, 0.8), transparent);
    animation: streamFlow 8s linear infinite;
}

.stream-1 {
    left: 20%;
    top: 10%;
    animation-delay: 0s;
}

.stream-2 {
    left: 70%;
    top: 30%;
    animation-delay: 2s;
}

.stream-3 {
    left: 45%;
    top: 60%;
    animation-delay: 4s;
}

.neural-network {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}

.neural-node {
    position: absolute;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(59, 130, 246, 1), rgba(59, 130, 246, 0.3));
    animation: nodePulse 3s ease-in-out infinite;
    filter: drop-shadow(0 0 10px rgba(59, 130, 246, 0.8));
}

.node-1 { top: 25%; left: 25%; animation-delay: 0s; }
.node-2 { top: 45%; right: 30%; animation-delay: 1s; }
.node-3 { bottom: 30%; left: 60%; animation-delay: 2s; }

.neural-connection {
    position: absolute;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(59, 130, 246, 0.5), transparent);
    animation: connectionPulse 4s ease-in-out infinite;
}

.connection-1 {
    top: 30%;
    left: 30%;
    width: 200px;
    transform: rotate(45deg);
    animation-delay: 0.5s;
}

.connection-2 {
    bottom: 35%;
    left: 40%;
    width: 150px;
    transform: rotate(-30deg);
    animation-delay: 2.5s;
}

.cyber-breach-content {
    position: relative;
    z-index: 10;
    max-width: 1000px;
    margin: 0 auto;
}

.cyber-header {
    text-align: center;
    margin-bottom: 3rem;
}

.header-glow {
    position: relative;
    display: inline-block;
    padding: 2rem;
    border-radius: 10px;
    background: radial-gradient(ellipse at center, rgba(59, 130, 246, 0.2), transparent);
}

.cyber-title {
    font-family: 'Orbitron', 'Russo One', monospace;
    font-size: 2.5rem;
    font-weight: 900;
    color: #ffffff;
    margin: 0 0 1rem 0;
    text-shadow: 0 0 20px rgba(59, 130, 246, 0.8);
    animation: titleGlow 3s ease-in-out infinite;
}

.title-bracket {
    color: #3b82f6;
    font-size: 3rem;
    animation: bracketFlicker 2s ease-in-out infinite;
}

.title-main {
    background: linear-gradient(135deg, #ffffff, #3b82f6);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.cyber-subtitle {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    font-family: 'Ubuntu Mono', monospace;
    font-size: 1rem;
    color: #64748b;
    text-transform: uppercase;
    letter-spacing: 2px;
}

.loading-dots {
    display: flex;
    gap: 4px;
}

.loading-dots span {
    width: 4px;
    height: 4px;
    background: #3b82f6;
    border-radius: 50%;
    /* Removed dotPulse animation */
}

.loading-dots span:nth-child(2) { animation-delay: 0.2s; }
.loading-dots span:nth-child(3) { animation-delay: 0.4s; }

/* Scanner Interface */
.cyber-scanner-interface {
    background: rgba(0, 0, 0, 0.8);
    border-radius: 15px;
    border: 2px solid rgba(59, 130, 246, 0.3);
    backdrop-filter: blur(20px);
    overflow: hidden;
    box-shadow: 
        0 0 50px rgba(59, 130, 246, 0.3),
        inset 0 0 20px rgba(0, 0, 0, 0.5);
}

.scanner-terminal {
    background: linear-gradient(135deg, rgba(15, 23, 42, 0.9), rgba(0, 0, 0, 0.9));
    border-radius: 13px;
}

.terminal-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 1rem 1.5rem;
    background: rgba(30, 41, 59, 0.8);
    border-bottom: 1px solid rgba(59, 130, 246, 0.2);
}

.terminal-controls {
    display: flex;
    gap: 8px;
}

.control-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
}

.control-dot.red { background: #ef4444; }
.control-dot.yellow { background: #eab308; }
.control-dot.green { background: #10b981; }

.terminal-title {
    font-family: 'Ubuntu Mono', monospace;
    font-size: 0.9rem;
    color: #3b82f6;
    font-weight: 600;
    letter-spacing: 1px;
}

.terminal-body {
    padding: 2rem;
}

.password-input-section {
    margin-bottom: 2rem;
}

.input-label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin-bottom: 1rem;
    font-family: 'Ubuntu Mono', monospace;
    font-size: 0.9rem;
    color: #64748b;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.label-icon {
    font-size: 1.2rem;
}

.cyber-input-container {
    position: relative;
    display: flex;
    align-items: center;
    height: 60px;
}

.cyber-input {
    width: 100%;
    padding: 1rem 4rem 1rem 1.5rem;
    background: rgba(15, 23, 42, 0.8);
    border: 2px solid rgba(59, 130, 246, 0.3);
    border-radius: 8px;
    color: #ffffff;
    font-family: 'Ubuntu Mono', monospace;
    font-size: 1.1rem;
    outline: none;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
    caret-color: #00ff00;
    height: 100%;
    box-sizing: border-box;
}

.cyber-input:focus {
    border-color: #3b82f6;
    box-shadow: 
        0 0 20px rgba(59, 130, 246, 0.5),
        inset 0 0 10px rgba(59, 130, 246, 0.1);
    background: rgba(15, 23, 42, 0.9);
}

.cyber-input::placeholder {
    color: #475569;
    font-style: italic;
}

.input-effects {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
    border-radius: 8px;
    overflow: hidden;
}

.input-glow {
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: linear-gradient(45deg, transparent, rgba(59, 130, 246, 0.3), transparent);
    border-radius: 8px;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.cyber-input:focus + .input-effects .input-glow {
    opacity: 1;
    animation: inputGlow 2s ease-in-out infinite;
}

.typing-cursor {
    display: none;
}

.cyber-input:focus + .input-effects .typing-cursor {
    display: none;
}

.visibility-toggle {
    position: absolute;
    right: 1rem;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    color: rgba(255, 255, 255, 0.6);
    cursor: pointer;
    padding: 0.5rem;
    border-radius: 4px;
    transition: color 0.3s ease, background-color 0.3s ease;
    z-index: 10;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
}

.visibility-toggle:hover {
    color: #3b82f6;
    background: rgba(59, 130, 246, 0.1);
}

.eye-icon {
    transition: transform 0.3s ease;
}

.visibility-toggle:hover .eye-icon {
    transform: scale(1.1);
}

/* Password Analysis */
.password-analysis {
    margin: 2rem 0;
    padding: 1.5rem;
    background: rgba(30, 41, 59, 0.6);
    border: 1px solid rgba(59, 130, 246, 0.2);
    border-radius: 10px;
    backdrop-filter: blur(10px);
}

.analysis-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.analysis-title {
    font-family: 'Ubuntu Mono', monospace;
    font-size: 0.9rem;
    color: #3b82f6;
    font-weight: 600;
    letter-spacing: 1px;
}

.strength-status {
    font-family: 'Ubuntu Mono', monospace;
    font-size: 0.9rem;
    font-weight: 600;
    padding: 0.25rem 0.75rem;
    border-radius: 4px;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.strength-meter {
    position: relative;
    height: 12px;
    background: rgba(55, 65, 81, 0.6);
    border-radius: 6px;
    overflow: hidden;
    margin-bottom: 1rem;
}

.meter-segments {
    display: flex;
    height: 100%;
    position: absolute;
    width: 100%;
    z-index: 1;
}

.segment {
    flex: 1;
    border-right: 1px solid rgba(0, 0, 0, 0.3);
    transition: background-color 0.3s ease;
}

.segment:last-child {
    border-right: none;
}

.meter-fill {
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    border-radius: 6px;
    transition: width 0.6s ease;
    width: 0%;
    z-index: 2;
}

.security-requirements {
    display: grid;
    gap: 0.5rem;
    font-size: 0.85rem;
}

.req-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    color: #6b7280;
    font-family: 'Ubuntu Mono', monospace;
    transition: all 0.3s ease;
}

.req-item.satisfied {
    color: #10b981;
}

.req-item.satisfied .req-icon {
    color: #10b981;
    transform: scale(1.2);
}

.req-icon {
    font-size: 0.8rem;
    transition: all 0.3s ease;
}

/* Scan Button */
.scan-button-container {
    margin: 2rem 0;
    text-align: center;
}

.cyber-scan-button {
    position: relative;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.8), rgba(147, 51, 234, 0.6));
    border: 2px solid #3b82f6;
    border-radius: 10px;
    padding: 1rem 2rem;
    cursor: pointer;
    transition: all 0.3s ease;
    overflow: hidden;
    min-width: 300px;
    backdrop-filter: blur(10px);
}

.cyber-scan-button:hover {
    transform: scale(1.05);
    box-shadow: 
        0 0 30px rgba(59, 130, 246, 0.6),
        0 0 60px rgba(59, 130, 246, 0.3);
    border-color: #60a5fa;
}

.cyber-scan-button:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

.button-content {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 1rem;
    font-family: 'Orbitron', 'Ubuntu', sans-serif;
    font-weight: 600;
    font-size: 1.1rem;
    color: #ffffff;
    text-transform: uppercase;
    letter-spacing: 1px;
    position: relative;
    z-index: 2;
}

.button-icon {
    font-size: 1.3rem;
    animation: iconPulse 2s ease-in-out infinite;
}

.button-arrow {
    font-size: 1.2rem;
    transition: transform 0.3s ease;
}

.cyber-scan-button:hover .button-arrow {
    transform: translateX(5px);
}

.button-effects {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
}

.scanning-line {
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
    transition: left 0.6s ease;
}

.cyber-scan-button.scanning .scanning-line {
    left: 100%;
}

.energy-pulse {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: radial-gradient(circle, rgba(59, 130, 246, 0.6), transparent);
    border-radius: 50%;
    opacity: 0;
}

.cyber-scan-button.scanning .energy-pulse {
    animation: energyPulse 1s ease-out;
}

/* Results Terminal */
.results-terminal {
    margin-top: 2rem;
    background: rgba(15, 23, 42, 0.9);
    border: 1px solid rgba(59, 130, 246, 0.3);
    border-radius: 10px;
    overflow: hidden;
    animation: slideInUp 0.5s ease-out;
}

.results-header {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem 1.5rem;
    background: rgba(30, 41, 59, 0.8);
    border-bottom: 1px solid rgba(59, 130, 246, 0.2);
}

.results-icon {
    font-size: 1.5rem;
    /* Removed spinning animation */
}

.results-title {
    font-family: 'Ubuntu Mono', monospace;
    font-size: 1rem;
    font-weight: 600;
    color: #3b82f6;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.results-timestamp {
    font-family: 'Ubuntu Mono', monospace;
    font-size: 0.8rem;
    color: #64748b;
    margin-left: auto;
}

.results-content {
    padding: 1.5rem;
}

/* Security Badges */
.cyber-security-badges {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
    margin-top: 2rem;
    padding: 2rem 0;
}

/* Add margin-left to the first badge (ENCRYPTED) */
.security-badge:nth-child(1) {
    margin-left: 2rem;
}

/* Add margin-right to the third badge (PRIVACY) */
.security-badge:nth-child(3) {
    margin-right: 2rem;
}

/* Responsive adjustments for margins */
@media (max-width: 768px) {
    .security-badge:nth-child(1) {
        margin-left: 1rem;
    }

    .security-badge:nth-child(3) {
        margin-right: 1rem;
    }
}

@media (max-width: 480px) {
    .security-badge:nth-child(1),
    .security-badge:nth-child(3) {
        margin-left: 0;
        margin-right: 0;
    }
}

.security-badge {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem;
    background: rgba(15, 23, 42, 0.85);
    border: 1px solid rgba(59, 130, 246, 0.4);
    border-radius: 8px;
    backdrop-filter: blur(15px);
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
}

.security-badge:hover {
    transform: translateY(-2px);
    border-color: #3b82f6;
    box-shadow: 0 5px 20px rgba(59, 130, 246, 0.3);
}

.badge-icon {
    font-size: 1.5rem;
    /* Removed spinning animation */
}

.badge-text {
    font-family: 'Ubuntu', sans-serif;
}

.badge-title {
    font-size: 0.9rem;
    font-weight: 600;
    color: #ffffff;
    margin-bottom: 0.25rem;
}

.badge-desc {
    font-size: 0.75rem;
    color: #64748b;
    text-transform: uppercase;
    letter-spacing: 1px;
}

/* Animations */
@keyframes gridPulse {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 0.6; }
}

@keyframes streamFlow {
    0% { transform: translateY(-100px); opacity: 0; }
    50% { opacity: 1; }
    100% { transform: translateY(calc(100vh + 100px)); opacity: 0; }
}

@keyframes nodePulse {
    0%, 100% { 
        transform: scale(1);
        filter: drop-shadow(0 0 10px rgba(59, 130, 246, 0.8));
    }
    50% { 
        transform: scale(1.3);
        filter: drop-shadow(0 0 20px rgba(59, 130, 246, 1));
    }
}

@keyframes connectionPulse {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 1; }
}

@keyframes titleGlow {
    0%, 100% { text-shadow: 0 0 20px rgba(59, 130, 246, 0.8); }
    50% { text-shadow: 0 0 30px rgba(59, 130, 246, 1), 0 0 40px rgba(59, 130, 246, 0.6); }
}

@keyframes bracketFlicker {
    0%, 100% { opacity: 1; }
    25% { opacity: 0.7; }
    50% { opacity: 1; }
    75% { opacity: 0.8; }
}

@keyframes dotPulse {
    0%, 100% { opacity: 0.3; transform: scale(1); }
    50% { opacity: 1; transform: scale(1.2); }
}

@keyframes inputGlow {
    0%, 100% { opacity: 0; }
    50% { opacity: 1; }
}

@keyframes cursorBlink {
    0%, 50% { opacity: 1; }
    51%, 100% { opacity: 0; }
}

@keyframes energyPulse {
    0% { 
        width: 0; 
        height: 0; 
        opacity: 1;
        transform: translate(-50%, -50%);
    }
    100% { 
        width: 200px; 
        height: 200px; 
        opacity: 0;
        transform: translate(-50%, -50%);
    }
}

/* Removed badgeGlow animation */

@keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

/* Responsive Design */
@media (max-width: 768px) {
    .cyber-title {
        font-size: 2rem;
    }

    .title-bracket {
        font-size: 2.5rem;
    }

    .terminal-body {
        padding: 1rem;
    }

    .cyber-security-badges {
        grid-template-columns: 1fr;
    }

    .cyber-scan-button {
        min-width: 250px;
    }
}

/* Cyber Animation Effects */
.scan-animation {
    display: none;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(90deg, transparent, rgba(59, 130, 246, 0.3), transparent);
    animation: scanning 1.5s infinite;
    border-radius: inherit;
}

@keyframes scanning {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

/* Additional Results Styling */
.scan-result {
    border-radius: 8px;
    font-family: 'Ubuntu', sans-serif;
}

.scan-result.error {
    animation: shake 0.5s ease-in-out;
}

.scan-result.breached {
    border-left: 4px solid #ef4444;
}

.scan-result.clean {
    border-left: 4px solid #10b981;
}

@keyframes shake {
    0%, 100% { transform: translateX(0); }
    25% { transform: translateX(-5px); }
    75% { transform: translateX(5px); }
}

@keyframes pulse {
    0%, 100% { opacity: 1; transform: scale(1); }
    50% { opacity: 0.7; transform: scale(1.1); }
}

/* ===========================================
   BUG REPORT PAGE STYLES
   =========================================== */

/* Bug Report Container */
.cyber-bug-report-container {
    min-height: 100vh;
    background: linear-gradient(135deg, #0f172a 0%, #1e293b 50%, #334155 100%);
    position: relative;
    overflow: hidden;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 2rem 1rem;
}

/* Background Effects */
.cyber-bg-effects {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
    z-index: 0;
}

.cyber-grid {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
        linear-gradient(rgba(0, 255, 255, 0.03) 1px, transparent 1px),
        linear-gradient(90deg, rgba(0, 255, 255, 0.03) 1px, transparent 1px);
    background-size: 50px 50px;
    animation: gridMove 20s linear infinite;
}

@keyframes gridMove {
    0% { transform: translate(0, 0); }
    100% { transform: translate(50px, 50px); }
}

.cyber-particles {
    position: absolute;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 20% 30%, rgba(0, 255, 255, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 80% 70%, rgba(255, 0, 127, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 40% 80%, rgba(0, 255, 0, 0.05) 0%, transparent 50%);
    animation: particleFloat 8s ease-in-out infinite;
}

@keyframes particleFloat {
    0%, 100% { transform: translateY(0px) scale(1); opacity: 0.3; }
    50% { transform: translateY(-20px) scale(1.1); opacity: 0.6; }
}

.gradient-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
        45deg,
        rgba(0, 255, 255, 0.05) 0%,
        transparent 25%,
        transparent 75%,
        rgba(255, 0, 127, 0.05) 100%
    );
    animation: gradientShift 6s ease-in-out infinite;
}

@keyframes gradientShift {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 0.7; }
}

/* Neural Network Animation */
.neural-network {
    position: absolute;
    top: 10%;
    left: 10%;
    right: 10%;
    bottom: 10%;
    pointer-events: none;
}

.neural-node {
    position: absolute;
    width: 8px;
    height: 8px;
    background: radial-gradient(circle, #00ffff 0%, transparent 70%);
    border-radius: 50%;
    animation: nodePulse 2s ease-in-out infinite;
}

.neural-node.node-1 { top: 20%; left: 15%; animation-delay: 0s; }
.neural-node.node-2 { top: 60%; left: 75%; animation-delay: 0.7s; }
.neural-node.node-3 { top: 80%; left: 30%; animation-delay: 1.4s; }

@keyframes nodePulse {
    0%, 100% { opacity: 0.4; transform: scale(1); }
    50% { opacity: 1; transform: scale(1.5); }
}

.neural-connection {
    position: absolute;
    height: 1px;
    background: linear-gradient(90deg, transparent, #00ffff, transparent);
    animation: connectionFlow 3s linear infinite;
}

.neural-connection.connection-1 {
    top: 22%;
    left: 16%;
    width: 400px;
    transform: rotate(45deg);
}

.neural-connection.connection-2 {
    top: 62%;
    left: 32%;
    width: 300px;
    transform: rotate(-30deg);
    animation-delay: 1s;
}

@keyframes connectionFlow {
    0% { opacity: 0; }
    10% { opacity: 1; }
    90% { opacity: 1; }
    100% { opacity: 0; }
}

/* Main Content */
.bug-report-content {
    position: relative;
    z-index: 10;
    width: 100%;
    max-width: 1000px;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

/* Cyber Header */
.cyber-header {
    text-align: center;
    margin-bottom: 2rem;
}

.status-indicator {
    display: inline-block;
    padding: 0.5rem 1.5rem;
    background: rgba(0, 255, 255, 0.1);
    border: 1px solid rgba(0, 255, 255, 0.3);
    border-radius: 25px;
    margin-bottom: 1rem;
    animation: statusGlow 2s ease-in-out infinite;
}

@keyframes statusGlow {
    0%, 100% { box-shadow: 0 0 10px rgba(0, 255, 255, 0.3); }
    50% { box-shadow: 0 0 20px rgba(0, 255, 255, 0.6); }
}

.status-text {
    color: #00ffff;
    font-family: 'Ubuntu Mono', monospace;
    font-size: 0.9rem;
    letter-spacing: 2px;
    font-weight: 600;
}

.report-title-display {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 1.5rem;
    flex-wrap: wrap;
}

.report-code {
    font-family: 'Ubuntu Mono', monospace;
    font-size: 3rem;
    font-weight: 900;
    color: #ff0080;
    text-shadow: 0 0 20px rgba(255, 0, 128, 0.5);
    animation: codeGlow 1.5s ease-in-out infinite;
}

@keyframes codeGlow {
    0%, 100% { text-shadow: 0 0 20px rgba(255, 0, 128, 0.5); }
    50% { text-shadow: 0 0 30px rgba(255, 0, 128, 0.8); }
}

.report-divider {
    width: 3px;
    height: 60px;
    background: linear-gradient(to bottom, transparent, #00ffff, transparent);
    animation: dividerPulse 2s ease-in-out infinite;
}

@keyframes dividerPulse {
    0%, 100% { opacity: 0.5; transform: scaleY(1); }
    50% { opacity: 1; transform: scaleY(1.2); }
}

.report-message {
    text-align: left;
}

.report-title {
    font-family: 'Ubuntu', sans-serif;
    font-size: 2.5rem;
    font-weight: 700;
    color: #ffffff;
    margin: 0;
    text-shadow: 0 0 10px rgba(255, 255, 255, 0.3);
}

.report-subtitle {
    font-family: 'Ubuntu', sans-serif;
    font-size: 1.1rem;
    color: #94a3b8;
    margin: 0.5rem 0 0 0;
}

/* Form Terminal */
.cyber-report-form {
    width: 100%;
    max-width: 800px;
    margin: 0 auto;
}

.form-terminal {
    background: rgba(15, 23, 42, 0.95);
    border: 1px solid rgba(0, 255, 255, 0.3);
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 
        0 0 30px rgba(0, 255, 255, 0.2),
        inset 0 0 20px rgba(0, 0, 0, 0.3);
    backdrop-filter: blur(10px);
}

.terminal-header {
    background: linear-gradient(90deg, #1e293b, #334155);
    padding: 1rem 1.5rem;
    display: flex;
    align-items: center;
    justify-content: space-between;
    border-bottom: 1px solid rgba(0, 255, 255, 0.2);
}

.terminal-controls {
    display: flex;
    gap: 0.5rem;
}

.control-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    animation: dotPulse 3s ease-in-out infinite;
}

.control-dot.red {
    background: #ef4444;
    animation-delay: 0s;
}

.control-dot.yellow {
    background: #f59e0b;
    animation-delay: 0.5s;
}

.control-dot.green {
    background: #10b981;
    animation-delay: 1s;
}

@keyframes dotPulse {
    0%, 100% { opacity: 0.6; transform: scale(1); }
    50% { opacity: 1; transform: scale(1.2); }
}

.terminal-title {
    font-family: 'Ubuntu Mono', monospace;
    color: #00ffff;
    font-size: 0.9rem;
    letter-spacing: 1px;
    font-weight: 600;
}

.terminal-body {
    padding: 2rem;
    background: rgba(0, 0, 0, 0.3);
}

/* Form Sections */
.form-section {
    margin-bottom: 2.5rem;
    border: 1px solid rgba(0, 255, 255, 0.1);
    border-radius: 8px;
    padding: 1.5rem;
    background: rgba(0, 255, 255, 0.02);
    transition: all 0.3s ease;
}

.form-section:hover {
    border-color: rgba(0, 255, 255, 0.3);
    background: rgba(0, 255, 255, 0.05);
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.1);
}

.section-header {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    margin-bottom: 1.5rem;
    padding-bottom: 0.5rem;
    border-bottom: 1px solid rgba(0, 255, 255, 0.2);
}

.section-icon {
    font-size: 1.2rem;
    filter: drop-shadow(0 0 5px rgba(0, 255, 255, 0.5));
}

.section-title {
    font-family: 'Ubuntu Mono', monospace;
    color: #00ffff;
    font-size: 1rem;
    font-weight: 600;
    letter-spacing: 1px;
    text-shadow: 0 0 5px rgba(0, 255, 255, 0.3);
}

/* Form Groups */
.form-group {
    margin-bottom: 1.5rem;
}

.cyber-label {
    display: block;
    width: 100%;
}

.label-text {
    display: block;
    font-family: 'Ubuntu Mono', monospace;
    color: #94a3b8;
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
    letter-spacing: 1px;
    text-transform: uppercase;
}

/* Form Inputs */
.cyber-input,
.cyber-select,
.cyber-textarea {
    width: 100%;
    padding: 0.75rem 1rem;
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(0, 255, 255, 0.3);
    border-radius: 6px;
    color: #ffffff;
    font-family: 'Ubuntu', sans-serif;
    font-size: 1rem;
    transition: all 0.3s ease;
    box-sizing: border-box;
}

.cyber-input:focus,
.cyber-select:focus,
.cyber-textarea:focus {
    outline: none;
    border-color: #00ffff;
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.3);
    background: rgba(0, 0, 0, 0.6);
}

.cyber-input::placeholder,
.cyber-textarea::placeholder {
    color: #64748b;
    opacity: 0.8;
}

.cyber-select {
    cursor: pointer;
}

.cyber-select option {
    background: #1e293b;
    color: #ffffff;
    padding: 0.5rem;
}

.cyber-textarea {
    resize: vertical;
    min-height: 120px;
    line-height: 1.5;
}

/* Submit Button */
.submit-container {
    display: flex;
    justify-content: center;
    margin-bottom: 1.5rem;
}

.cyber-submit-btn {
    position: relative;
    background: linear-gradient(135deg, #ff0080, #7c3aed);
    border: none;
    border-radius: 8px;
    padding: 1rem 2rem;
    color: #ffffff;
    font-family: 'Ubuntu', sans-serif;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    overflow: hidden;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(255, 0, 128, 0.3);
}

.cyber-submit-btn:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 6px 25px rgba(255, 0, 128, 0.5);
}

.cyber-submit-btn:active {
    transform: translateY(0);
}

.cyber-submit-btn:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
}

.button-content {
    position: relative;
    z-index: 2;
    display: flex;
    align-items: center;
    gap: 0.75rem;
}

.button-icon {
    font-size: 1.2rem;
    filter: drop-shadow(0 0 5px rgba(255, 255, 255, 0.5));
}

.button-text {
    letter-spacing: 1px;
    text-shadow: 0 0 5px rgba(255, 255, 255, 0.3);
}

.button-arrow {
    font-size: 1.3rem;
    transition: transform 0.3s ease;
}

.cyber-submit-btn:hover .button-arrow {
    transform: translateX(3px);
}

.button-effects {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
}

.scanning-line {
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    animation: scanningButton 2s ease-in-out infinite;
}

@keyframes scanningButton {
    0% { left: -100%; }
    100% { left: 100%; }
}

.energy-pulse {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.8), transparent);
    border-radius: 50%;
    animation: energyPulse 1.5s ease-out infinite;
    transform: translate(-50%, -50%);
}

@keyframes energyPulse {
    0% { width: 0; height: 0; opacity: 1; }
    100% { width: 200px; height: 200px; opacity: 0; }
}

/* Transmission Status */
.transmission-status {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    justify-content: center;
    padding: 1rem;
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(0, 255, 255, 0.3);
    border-radius: 8px;
    color: #00ffff;
    font-family: 'Ubuntu Mono', monospace;
    animation: statusFade 0.5s ease-in;
}

@keyframes statusFade {
    0% { opacity: 0; transform: translateY(10px); }
    100% { opacity: 1; transform: translateY(0); }
}

.status-icon {
    font-size: 1.5rem;
    animation: iconSpin 1s linear infinite;
}

@keyframes iconSpin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.status-message {
    font-size: 1rem;
    letter-spacing: 1px;
}

/* Info Panel */
.bug-info-panel {
    background: rgba(15, 23, 42, 0.9);
    border: 1px solid rgba(0, 255, 255, 0.2);
    border-radius: 12px;
    padding: 1.5rem;
    max-width: 500px;
    margin: 0 auto;
    backdrop-filter: blur(5px);
}

.info-header {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    margin-bottom: 1rem;
    padding-bottom: 0.75rem;
    border-bottom: 1px solid rgba(0, 255, 255, 0.2);
}

.info-icon {
    font-size: 1.3rem;
    filter: drop-shadow(0 0 5px rgba(0, 255, 255, 0.5));
}

.info-header span {
    font-family: 'Ubuntu Mono', monospace;
    color: #00ffff;
    font-weight: 600;
    letter-spacing: 1px;
    font-size: 1rem;
}

.info-content {
    display: flex;
    flex-direction: column;
    gap: 0.75rem;
}

.guideline-item {
    display: flex;
    align-items: flex-start;
    gap: 0.75rem;
    padding: 0.5rem;
    border-radius: 6px;
    transition: all 0.3s ease;
}

.guideline-item:hover {
    background: rgba(0, 255, 255, 0.05);
    transform: translateX(5px);
}

.guideline-icon {
    font-size: 1.1rem;
    flex-shrink: 0;
    margin-top: 0.1rem;
}

.guideline-item span:last-child {
    color: #e2e8f0;
    font-family: 'Ubuntu', sans-serif;
    font-size: 0.95rem;
    line-height: 1.4;
}

/* Cyber Overlay Effects */
.cyber-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
    z-index: 5;
}

.scan-line {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient(90deg, transparent, #00ffff, transparent);
    animation: scanLine 4s ease-in-out infinite;
}

@keyframes scanLine {
    0% { top: 0%; opacity: 0; }
    50% { opacity: 1; }
    100% { top: 100%; opacity: 0; }
}

.noise-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml;base64,');
    animation: noiseShift 0.5s steps(10) infinite;
}

@keyframes noiseShift {
    0% { transform: translateX(0px) translateY(0px); }
    10% { transform: translateX(-1px) translateY(-1px); }
    20% { transform: translateX(1px) translateY(0px); }
    30% { transform: translateX(0px) translateY(1px); }
    40% { transform: translateX(-1px) translateY(0px); }
    50% { transform: translateX(1px) translateY(-1px); }
    60% { transform: translateX(0px) translateY(0px); }
    70% { transform: translateX(-1px) translateY(1px); }
    80% { transform: translateX(1px) translateY(0px); }
    90% { transform: translateX(0px) translateY(-1px); }
    100% { transform: translateX(0px) translateY(0px); }
}

/* Responsive Design */
@media (max-width: 768px) {
    .cyber-bug-report-container {
        padding: 1rem 0.5rem;
    }

    .report-title-display {
        flex-direction: column;
        gap: 1rem;
    }

    .report-code {
        font-size: 2rem;
    }

    .report-title {
        font-size: 1.8rem;
        text-align: center;
    }

    .report-divider {
        width: 60px;
        height: 3px;
        transform: rotate(90deg);
    }

    .terminal-header {
        padding: 0.75rem 1rem;
        flex-direction: column;
        gap: 0.5rem;
        text-align: center;
    }

    .terminal-body {
        padding: 1.5rem 1rem;
    }

    .form-section {
        padding: 1rem;
    }

    .bug-info-panel {
        margin-top: 1rem;
    }
}

/* Cyber Pulse Animation */
@keyframes cyberPulse {
    0% { box-shadow: 0 0 15px rgba(0, 255, 255, 0.3); }
    50% { box-shadow: 0 0 25px rgba(0, 255, 255, 0.6); }
    100% { box-shadow: 0 0 15px rgba(0, 255, 255, 0.3); }
}

/* Mobile Bottom Navigation - Clean and Modern */
.mobile-bottom-nav {
    position: fixed !important;
    bottom: 20px !important;
    left: 50% !important;
    transform: translateX(-50%) !important;
    z-index: 9999 !important;
    display: none !important;
    width: auto !important;
    max-width: 90vw !important;
}

.mobile-nav-container {
    display: flex !important;
    align-items: center !important;
    justify-content: center !important;
    gap: 8px !important;
    background: rgba(0, 0, 0, 0.9) !important;
    backdrop-filter: blur(20px) !important;
    border: 1px solid rgba(255, 255, 255, 0.2) !important;
    border-radius: 25px !important;
    padding: 12px 20px !important;
    box-shadow: 
        0 10px 40px rgba(0, 0, 0, 0.6),
        0 0 0 1px rgba(255, 255, 255, 0.1),
        inset 0 1px 0 rgba(255, 255, 255, 0.2) !important;
}

.mobile-nav-item {
    display: flex !important;
    flex-direction: column !important;
    align-items: center !important;
    gap: 4px !important;
    text-decoration: none !important;
    color: rgba(255, 255, 255, 0.7) !important;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1) !important;
    padding: 8px 12px !important;
    border-radius: 16px !important;
    min-width: 60px !important;
}

.mobile-nav-item:hover {
    color: #fff !important;
    transform: translateY(-3px) !important;
}

.mobile-nav-item.active {
    color: #fff !important;
}

.nav-icon-container {
    display: flex !important;
    align-items: center !important;
    justify-content: center !important;
    width: 44px !important;
    height: 44px !important;
    border-radius: 14px !important;
    background: rgba(255, 255, 255, 0.1) !important;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1) !important;
}

.mobile-nav-item.active .nav-icon-container {
    background: linear-gradient(135deg, #00bcd4, #2196f3) !important;
    box-shadow: 0 8px 25px rgba(0, 188, 212, 0.4) !important;
    transform: scale(1.05) !important;
}

.mobile-nav-item:hover .nav-icon-container {
    background: rgba(255, 255, 255, 0.2) !important;
    transform: scale(1.1) !important;
}

.mobile-nav-item.active:hover .nav-icon-container {
    transform: scale(1.15) !important;
}

.nav-icon {
    width: 20px !important;
    height: 20px !important;
    transition: all 0.3s ease !important;
}

.nav-label {
    font-size: 11px !important;
    font-weight: 700 !important;
    font-family: 'Ubuntu', sans-serif !important;
    transition: all 0.3s ease !important;
    letter-spacing: 0.5px !important;
    text-transform: uppercase !important;
}

/* Show mobile navigation only on mobile devices */
@media (max-width: 768px) {
    .mobile-bottom-nav {
        display: block !important;
        animation: mobileNavSlideIn 0.8s cubic-bezier(0.4, 0, 0.2, 1) 1.2s both !important;
    }

    /* Hide desktop navigation on mobile */
    .desktop-nav {
        display: none !important;
    }
}

@keyframes mobileNavSlideIn {
    from {
        opacity: 0;
        transform: translateX(-50%) translateY(100px);
    }
    to {
        opacity: 1;
        transform: translateX(-50%) translateY(0);
    }
}

