/* Import Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Passion+One:wght@400;700;900&family=Russo+One&family=Rubik+Mono+One&family=Ubuntu:wght@400;700&display=swap');

/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --bg-primary: #000;
    --bg-secondary: #0f1729;
    --text-primary: #fff;
    --text-secondary: rgba(255, 255, 255, 0.8);
    --accent-primary: #3b82f6;
    --accent-secondary: #00ffff;
    --border-color: rgba(255, 255, 255, 0.1);
    --glass-bg: rgba(255, 255, 255, 0.05);
}

.light-mode {
    --bg-primary: #f8fafc;
    --bg-secondary: #e2e8f0;
    --text-primary: #1e293b;
    --text-secondary: #475569;
    --accent-primary: #3b82f6;
    --accent-secondary: #0891b2;
    --border-color: rgba(30, 41, 59, 0.15);
    --glass-bg: rgba(248, 250, 252, 0.85);
    --light-shadow: 0 4px 6px rgba(0, 0, 0, 0.07);
    --light-shadow-lg: 0 10px 25px rgba(0, 0, 0, 0.1);
    --light-accent-bg: rgba(59, 130, 246, 0.08);
    --light-success: #16a34a;
    --light-warning: #ea580c;
    --light-danger: #dc2626;
    --light-card-bg: rgba(255, 255, 255, 0.7);
    --light-input-bg: rgba(255, 255, 255, 0.9);
}

body {
    font-family: 'Ubuntu', sans-serif;
    background-color: var(--bg-primary);
    color: var(--text-primary);
    overflow-x: hidden;
    line-height: 1.6;
    transition: background-color 0.3s ease, color 0.3s ease;
}

/* FOUC Prevention */
.content-wrapper {
    opacity: 0;
    transition: opacity 0.5s ease;
}

.content-wrapper.loaded {
    opacity: 1;
}

/* Initial Loading Screen */
.initial-loading {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #000 0%, #0f1729 100%);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    z-index: 10000;
    opacity: 1;
    transition: opacity 0.5s ease;
}

.initial-loading.hidden {
    opacity: 0;
    pointer-events: none;
}

.initial-spinner {
    width: 50px;
    height: 50px;
    border: 3px solid rgba(59, 130, 246, 0.3);
    border-top: 3px solid var(--accent-primary);
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin-bottom: 1rem;
}

.initial-text {
    color: var(--text-primary);
    font-size: 1.1rem;
    font-weight: 500;
    animation: pulse 2s ease-in-out infinite;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

/* Page Management */
.page {
    display: none;
    opacity: 0;
    transform: translateY(20px);
    transition: all 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

.page.active {
    display: block;
    opacity: 1;
    transform: translateY(0);
}

/* Home-specific content isolation */
.security-section,
.breach-check-section,
.password-generator-section {
    display: block;
    animation: slideInUp 0.8s ease-out;
}

#home-page.active .security-section,
#home-page.active .breach-check-section,
#home-page.active .password-generator-section {
    display: block;
    animation: slideInUp 0.8s ease-out;
}

@keyframes slideInUp {
    from {
        opacity: 0;
        transform: translateY(40px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Page Transition Styles */
.page-transition {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.9), rgba(15, 23, 42, 0.9));
    z-index: 9999;
    display: flex;
    align-items: center;
    justify-content: center;
    animation: transitionIn 0.3s ease-out;
}

.light-mode .page-transition {
    background: linear-gradient(135deg, rgba(248, 250, 252, 0.9), rgba(226, 232, 240, 0.9));
}

.transition-loader {
    text-align: center;
}

.loader-dots {
    display: flex;
    gap: 0.5rem;
    margin-bottom: 1rem;
}

.loader-dots span {
    width: 12px;
    height: 12px;
    background: var(--accent-secondary);
    border-radius: 50%;
    animation: dotPulse 1.5s ease-in-out infinite;
}

.loader-dots span:nth-child(1) { animation-delay: 0s; }
.loader-dots span:nth-child(2) { animation-delay: 0.5s; }
.loader-dots span:nth-child(3) { animation-delay: 1s; }

.page-fadeout {
    animation: fadeOut 0.3s ease-out forwards;
}

.page-fadein {
    animation: fadeIn 0.6s ease-out forwards;
}

/* Creator Section */
.creator-section {
    margin-bottom: 4rem;
    padding: 2rem 0;
    border-bottom: 1px solid var(--border-color);
}

.creator-card {
    background: var(--glass-bg);
    border: 1px solid var(--border-color);
    border-radius: 20px;
    padding: 2rem;
    backdrop-filter: blur(10px);
    transition: all 0.3s ease;
}

.creator-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 20px 40px rgba(59, 130, 246, 0.2);
}

.creator-info {
    display: grid;
    grid-template-columns: auto 1fr;
    gap: 2rem;
    align-items: center;
}

.creator-photo-container {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
}

.photo-placeholder {
    position: relative;
    width: 150px;
    height: 150px;
    background: linear-gradient(135deg, var(--accent-primary), var(--accent-secondary));
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    cursor: pointer;
    transition: all 0.3s ease;
    animation: profileGlow 4s ease-in-out infinite;
}

.photo-placeholder:hover {
    transform: scale(1.05);
}

.photo-icon {
    font-size: 4rem;
    color: white;
}

.photo-overlay {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    background: rgba(0, 0, 0, 0.8);
    color: white;
    text-align: center;
    padding: 0.5rem;
    font-size: 0.8rem;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.photo-placeholder:hover .photo-overlay {
    opacity: 1;
}

.creator-badge {
    background: linear-gradient(135deg, var(--accent-primary), var(--accent-secondary));
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-size: 0.9rem;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    animation: badgeFloat 3s ease-in-out infinite;
}

.creator-details {
    text-align: left;
}

.creator-name {
    font-family: 'Passion One', sans-serif;
    font-size: 2.5rem;
    color: var(--text-primary);
    margin-bottom: 0.5rem;
    text-shadow: 0 0 20px var(--accent-secondary);
    animation: nameGlow 3s ease-in-out infinite;
}

.creator-title {
    font-size: 1.2rem;
    color: var(--accent-primary);
    margin-bottom: 1rem;
    font-weight: bold;
}

.creator-description {
    color: var(--text-secondary);
    margin-bottom: 2rem;
    line-height: 1.6;
    font-size: 1rem;
}

/* Dynamic Stats Counters */
.dynamic-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 2rem;
    margin: 3rem 0;
    padding: 2rem 0;
}

.stat-counter {
    text-align: center;
    padding: 1.5rem;
    background: var(--glass-bg);
    border: 1px solid var(--border-color);
    border-radius: 15px;
    backdrop-filter: blur(10px);
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.stat-counter::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(59, 130, 246, 0.1), transparent);
    transition: left 0.5s ease;
}

.stat-counter:hover::before {
    left: 100%;
}

.stat-counter:hover {
    transform: translateY(-5px) scale(1.02);
    box-shadow: 0 10px 30px rgba(59, 130, 246, 0.3);
}

.counter {
    font-family: 'Passion One', sans-serif;
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--accent-primary);
    text-shadow: 0 0 20px var(--accent-secondary);
    margin-bottom: 0.5rem;
    animation: counterGlow 3s ease-in-out infinite;
}

.counter-label {
    color: var(--text-secondary);
    font-size: 0.9rem;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

@keyframes counterGlow {
    0%, 100% {
        text-shadow: 0 0 20px var(--accent-secondary);
    }
    50% {
        text-shadow: 0 0 30px var(--accent-primary), 0 0 40px var(--accent-secondary);
    }
}

/* Typing Text Animation */
.typing-text {
    position: relative;
    overflow: hidden;
}


@keyframes blink {
    0%, 50% { opacity: 1; }
    51%, 100% { opacity: 0; }
}

/* Floating Elements */
.floating-element {
    animation: float 6s ease-in-out infinite;
}

.floating-element:nth-child(2) {
    animation-delay: 2s;
}

.floating-element:nth-child(3) {
    animation-delay: 4s;
}

@keyframes float {
    0%, 100% {
        transform: translateY(0px);
    }
    50% {
        transform: translateY(-10px);
    }
}

.creator-social {
    display: flex;
    gap: 1rem;
    flex-wrap: wrap;
}

.social-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1.5rem;
    background: var(--glass-bg);
    border: 1px solid var(--border-color);
    border-radius: 25px;
    text-decoration: none;
    color: var(--text-primary);
    transition: all 0.3s ease;
    backdrop-filter: blur(5px);
}

.social-btn.linkedin:hover {
    background: rgba(0, 119, 181, 0.2);
    border-color: rgba(0, 119, 181, 0.5);
    transform: translateY(-2px);
}

.social-btn.instagram:hover {
    background: rgba(225, 48, 108, 0.2);
    border-color: rgba(225, 48, 108, 0.5);
    transform: translateY(-2px);
}

.social-btn.whatsapp:hover {
    background: rgba(37, 211, 102, 0.2);
    border-color: rgba(37, 211, 102, 0.5);
    transform: translateY(-2px);
}

/* Fixed Footer Security Indicators */
.security-indicators {
    display: flex;
    justify-content: center;
    gap: 1.5rem;
    flex-wrap: wrap;
    margin-top: 1rem;
}

.indicator-item {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1rem;
    background: rgba(16, 185, 129, 0.1);
    border: 1px solid rgba(16, 185, 129, 0.3);
    border-radius: 25px;
    font-size: 0.9rem;
    color: #10b981;
    backdrop-filter: blur(5px);
    transition: all 0.3s ease;
}

.light-mode .indicator-item {
    background: rgba(16, 185, 129, 0.2);
    color: #047857;
}

.indicator-item:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(16, 185, 129, 0.3);
}

.indicator-dot {
    width: 8px;
    height: 8px;
    background: #10b981;
    border-radius: 50%;
    animation: indicatorPulse 2s ease-in-out infinite;
}

.indicator-text {
    white-space: nowrap;
    font-weight: 500;
}

/* Initial Loading Screen - prevents FOUC */
.initial-loading {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #000, #0f1729);
    z-index: 10001;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    transition: opacity 0.5s ease;
}

.initial-loading.hidden {
    opacity: 0;
    pointer-events: none;
}

.initial-spinner {
    width: 60px;
    height: 60px;
    border: 4px solid rgba(59, 130, 246, 0.3);
    border-top: 4px solid #3b82f6;
    border-radius: 50%;
    animation: none;
    margin-bottom: 2rem;
}

.initial-text {
    color: #fff;
    font-family: 'Passion One', sans-serif;
    font-size: 1.5rem;
    text-align: center;
    animation: initialTextPulse 2s ease-in-out infinite;
}

/* Loading Screen Styles */
.loading-screen {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: #000;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 9999;
    transition: opacity 0.5s ease-out;
}

.loading-screen.fade-out {
    opacity: 0;
    pointer-events: none;
}

.loading-container {
    text-align: center;
    position: relative;
    z-index: 2;
}

.loading-logo {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 1rem;
    margin-bottom: 2rem;
    animation: logoGlow 2s ease-in-out infinite;
}

.loading-logo-b {
    font-family: 'Rubik Mono One', monospace;
    font-size: 4rem;
    color: #fff;
    transform: rotate(180deg);
    animation: logoSpin 2s ease-in-out infinite, logoGlow 2s ease-in-out infinite;
}

.loading-logo-text {
    display: flex;
    flex-direction: column;
    font-family: 'Russo One', sans-serif;
    color: #fff;
}

.loading-logo-text span {
    font-size: 1.5rem;
    line-height: 1.1;
    animation: textShimmer 2s ease-in-out infinite;
}

.loading-bar-container {
    width: 300px;
    height: 4px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    overflow: hidden;
    margin: 0 auto 1rem;
    position: relative;
}

.loading-bar {
    height: 100%;
    background: linear-gradient(90deg, #3b82f6, #9333ea, #3b82f6);
    background-size: 200% 100%;
    border-radius: 2px;
    animation: loadingProgress 3s ease-out forwards, shimmer 1.5s ease-in-out infinite;
}

.loading-text {
    font-family: 'Ubuntu', sans-serif;
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.7);
    animation: textFade 2s ease-in-out infinite;
}

.loading-particles {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
}

.loading-particle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: rgba(59, 130, 246, 0.6);
    border-radius: 50%;
    animation: particleRise 3s ease-in-out infinite;
}

/* ===== MODERN CYBER 404 PAGE STYLES ===== */
.cyber-404-container {
    position: relative;
    min-height: 100vh;
    background: linear-gradient(135deg, #0a0a0f 0%, #1a1a2e 50%, #16213e 100%);
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #e1e5f2;
    font-family: 'Ubuntu', -apple-system, BlinkMacSystemFont, sans-serif;
}

.cyber-bg-effects {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
}

.cyber-grid {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        linear-gradient(rgba(59, 130, 246, 0.1) 1px, transparent 1px),
        linear-gradient(90deg, rgba(59, 130, 246, 0.1) 1px, transparent 1px);
    background-size: 50px 50px;
    opacity: 0.5;
    animation: gridMove 20s linear infinite;
}

.cyber-particles {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle, rgba(59, 130, 246, 0.1) 1px, transparent 1px);
    background-size: 100px 100px;
    animation: particlesFloat 15s ease-in-out infinite;
}

.gradient-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(ellipse at center, transparent 0%, rgba(0, 0, 0, 0.3) 100%);
}

.error-404-content {
    position: relative;
    z-index: 10;
    text-align: center;
    max-width: 800px;
    padding: 3rem 2rem;
}

.status-indicator {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 3rem;
    opacity: 0;
    animation: fadeInUp 0.8s ease-out 0.2s forwards;
}

.status-text {
    font-size: 1rem;
    font-weight: 700;
    letter-spacing: 3px;
    color: #ef4444;
    text-transform: uppercase;
    text-align: center;
    border: 1px solid rgba(239, 68, 68, 0.3);
    padding: 0.75rem 2rem;
    border-radius: 6px;
    background: rgba(239, 68, 68, 0.05);
    backdrop-filter: blur(10px);
}

.error-code-display {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 3rem;
    margin-bottom: 3rem;
    opacity: 0;
    animation: fadeInUp 0.8s ease-out 0.4s forwards;
}

.error-code {
    font-size: clamp(6rem, 15vw, 12rem);
    font-weight: 900;
    background: linear-gradient(135deg, #3b82f6, #8b5cf6, #06b6d4);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    text-shadow: 0 0 50px rgba(59, 130, 246, 0.3);
    line-height: 1;
    font-family: 'Russo One', sans-serif;
    position: relative;
    cursor: default;
    transition: all 0.3s ease;
}

.error-code:hover {
    background: linear-gradient(135deg, #60a5fa, #a78bfa, #22d3ee);
    background-clip: text;
    -webkit-background-clip: text;
    text-shadow: 0 0 80px rgba(59, 130, 246, 0.5);
}

.error-divider {
    width: 2px;
    height: 120px;
    background: linear-gradient(to bottom, transparent, #3b82f6, transparent);
    animation: dividerGlow 3s ease-in-out infinite alternate;
}

.error-message {
    text-align: left;
}

.error-title {
    font-size: clamp(2rem, 5vw, 3.5rem);
    font-weight: 700;
    color: #f1f5f9;
    margin: 0 0 1rem 0;
    letter-spacing: 1px;
    font-family: 'Russo One', sans-serif;
}

.error-subtitle {
    font-size: 1.1rem;
    color: #94a3b8;
    margin: 0;
    line-height: 1.6;
}

.system-info-panel {
    background: rgba(15, 23, 42, 0.8);
    border: 1px solid rgba(59, 130, 246, 0.3);
    border-radius: 12px;
    padding: 2rem;
    margin: 3rem 0;
    backdrop-filter: blur(10px);
    opacity: 0;
    animation: fadeInUp 0.8s ease-out 0.6s forwards;
}

.info-header {
    display: flex;
    align-items: center;
    gap: 12px;
    margin-bottom: 1.5rem;
    color: #3b82f6;
    font-weight: 600;
    font-size: 0.9rem;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.info-icon {
    width: 16px;
    height: 16px;
    background: #3b82f6;
    border-radius: 3px;
    animation: iconPulse 2s ease-in-out infinite;
}

.info-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
}

.info-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0.75rem;
    background: rgba(30, 41, 59, 0.5);
    border-radius: 6px;
    border-left: 3px solid #3b82f6;
    transition: all 0.3s ease;
    cursor: pointer;
    position: relative;
    overflow: hidden;
}

.info-item::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(59, 130, 246, 0.1), transparent);
    transition: left 0.6s ease;
}

.info-item:hover::before {
    left: 100%;
}

.info-item .label {
    font-size: 0.8rem;
    color: #64748b;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.info-item .value {
    font-size: 0.9rem;
    color: #e2e8f0;
    font-weight: 600;
    font-family: 'Courier New', monospace;
    position: relative;
}

.info-item:nth-child(3) .value {
    animation: dataFlicker 3s ease-in-out infinite;
}

.info-item .value.error {
    color: #ef4444;
}

.action-panel {
    display: flex;
    gap: 1rem;
    justify-content: center;
    margin: 3rem 0;
    flex-wrap: wrap;
    opacity: 0;
    animation: fadeInUp 0.8s ease-out 0.8s forwards;
}

.cyber-action-btn {
    position: relative;
    background: transparent;
    border: 1px solid;
    border-radius: 8px;
    padding: 0;
    cursor: pointer;
    overflow: hidden;
    transition: all 0.3s ease;
    font-family: inherit;
}

.cyber-action-btn.primary {
    border-color: #3b82f6;
    color: #3b82f6;
}

.cyber-action-btn.secondary {
    border-color: #64748b;
    color: #64748b;
}

.btn-content {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 1rem 2rem;
    position: relative;
    z-index: 2;
}

.btn-icon {
    display: flex;
    align-items: center;
    justify-content: center;
    transition: transform 0.2s ease;
}

.btn-icon svg {
    transition: all 0.3s ease;
}

.btn-text {
    font-size: 0.9rem;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.btn-shine {
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transition: left 0.6s ease;
    z-index: 1;
}

.cyber-action-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
}

.cyber-action-btn:hover .btn-icon {
    transform: scale(1.1);
}

.cyber-action-btn.primary:hover {
    background: rgba(59, 130, 246, 0.1);
    border-color: #60a5fa;
    box-shadow: 0 10px 25px rgba(59, 130, 246, 0.2);
}

.cyber-action-btn.primary:hover .btn-icon svg {
    filter: drop-shadow(0 0 8px rgba(59, 130, 246, 0.6));
}

.cyber-action-btn.secondary:hover {
    background: rgba(100, 116, 139, 0.1);
    border-color: #94a3b8;
}

.cyber-action-btn.secondary:hover .btn-icon svg {
    filter: drop-shadow(0 0 8px rgba(100, 116, 139, 0.6));
}

.cyber-action-btn:hover .btn-shine {
    left: 100%;
}

.additional-info {
    margin-top: 3rem;
    opacity: 0;
    animation: fadeInUp 0.8s ease-out 1s forwards;
}

.help-text {
    color: #94a3b8;
    font-size: 0.9rem;
    margin-bottom: 1rem;
    line-height: 1.6;
}

.error-code-small {
    font-family: 'Courier New', monospace;
    font-size: 0.8rem;
    color: #475569;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.cyber-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 5;
}

.scan-line {
    position: absolute;
    top: -2px;
    left: 0;
    width: 100%;
    height: 2px;
    background: linear-gradient(90deg, transparent, #3b82f6, transparent);
    animation: scanLine 3s ease-in-out infinite;
}

.noise-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="100" height="100" viewBox="0 0 100 100"><defs><filter id="noise"><feTurbulence baseFrequency="0.9" numOctaves="1" stitchTiles="stitch"/><feColorMatrix type="saturate" values="0"/></filter></defs><rect width="100%" height="100%" filter="url(%23noise)" opacity="0.03"/></svg>');
}

/* ===== MODERN 404 ANIMATIONS ===== */
@keyframes gridMove {
    0% { transform: translate(0, 0); }
    100% { transform: translate(50px, 50px); }
}

@keyframes particlesFloat {
    0%, 100% { 
        transform: translateY(0px) rotate(0deg);
        opacity: 0.1;
    }
    50% { 
        transform: translateY(-20px) rotate(180deg);
        opacity: 0.3;
    }
}

@keyframes fadeInUp {
    0% {
        opacity: 0;
        transform: translateY(30px);
    }
    100% {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes statusPulse {
    0%, 100% { 
        opacity: 1;
        transform: scale(1);
    }
    50% { 
        opacity: 0.6;
        transform: scale(1.1);
    }
}

@keyframes dividerGlow {
    0% { 
        box-shadow: 0 0 10px rgba(59, 130, 246, 0.3);
    }
    100% { 
        box-shadow: 0 0 30px rgba(59, 130, 246, 0.8);
    }
}

@keyframes iconPulse {
    0%, 100% { 
        transform: scale(1);
        opacity: 1;
    }
    50% { 
        transform: scale(1.1);
        opacity: 0.8;
    }
}

@keyframes scanLine {
    0% { 
        top: -2px;
        opacity: 1;
    }
    50% { 
        opacity: 0.5;
    }
    100% { 
        top: 100%;
        opacity: 0;
    }
}


@keyframes dataFlicker {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.7; }
}

@keyframes codeScroll {
    0% { transform: translateY(0); }
    100% { transform: translateY(-20px); }
}

/* Responsive design for modern 404 page */
@media (max-width: 768px) {
    .error-404-content {
        padding: 2rem 1rem;
    }

    .error-code-display {
        flex-direction: column;
        gap: 1.5rem;
    }

    .error-divider {
        width: 100px;
        height: 2px;
    }

    .error-message {
        text-align: center;
    }

    .system-info-panel {
        padding: 1.5rem;
    }

    .info-grid {
        grid-template-columns: 1fr;
        gap: 0.5rem;
    }

    .action-panel {
        flex-direction: column;
        align-items: center;
    }

    .cyber-action-btn {
        width: 100%;
        max-width: 280px;
    }
}

@media (max-width: 480px) {
    .error-code {
        font-size: 4rem;
    }

    .error-title {
        font-size: 1.8rem;
    }

    .error-subtitle {
        font-size: 1rem;
    }

    .system-info-panel {
        padding: 1rem;
    }

    .info-item {
        flex-direction: column;
        align-items: flex-start;
        gap: 0.5rem;
    }
}

/* Background Animations */
.background-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
}

.hero-gradient {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(
        ellipse at center,
        rgba(52, 41, 211, 0.3) 0%,
        rgba(27, 21, 109, 0.1) 50%,
        transparent 100%
    );
    animation: breathe 20s ease-in-out infinite;
}

.floating-orb {
    position: absolute;
    border-radius: 50%;
    filter: blur(40px);
}

.orb-1 {
    top: 25%;
    left: 25%;
    width: 8rem;
    height: 8rem;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.3), rgba(147, 51, 234, 0.3));
    animation: float-1 8s ease-in-out infinite;
}

.orb-2 {
    top: 75%;
    right: 25%;
    width: 6rem;
    height: 6rem;
    background: linear-gradient(135deg, rgba(147, 51, 234, 0.2), rgba(59, 130, 246, 0.2));
    animation: float-2 10s ease-in-out infinite 2s;
}

/* Content Wrapper */
.content-wrapper {
    position: relative;
    z-index: 10;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    opacity: 0;
    transition: opacity 0.5s ease;
}

.content-wrapper.loaded {
    opacity: 1;
}

/* Page Management */
.page {
    display: none;
}

.page.active {
    display: block;
}

/* Home-specific content should only show on home page */
.security-section,
.breach-check-section,
.password-generator-section {
    display: none;
}

#home-page.active .security-section,
#home-page.active .breach-check-section,
#home-page.active .password-generator-section {
    display: block;
}

/* Header Styles */
.header {
    width: 100%;
    padding: 1.5rem 1rem;
    animation: slideInDown 0.8s ease-out;
}

.header-container {
    max-width: 1280px;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.logo-container {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    cursor: pointer;
    transition: transform 0.3s ease;
    animation: fadeInLeft 0.8s ease-out 0.2s both;
}

.logo-container:hover {
    transform: scale(1.05);
}

.logo-b {
    font-family: 'Rubik Mono One', monospace;
    font-size: 3rem;
    color: #fff;
    transform: rotate(180deg);
    animation: logoSpin 3s ease-in-out infinite;
}

.logo-text {
    display: flex;
    flex-direction: column;
    font-family: 'Russo One', sans-serif;
    font-weight: normal;
    color: #fff;
}

.logo-text span {
    font-size: 1.25rem;
    line-height: 1.1;
}

/* Navigation Styles */
.desktop-nav {
    display: none;
    animation: fadeInUp 0.8s ease-out 0.4s both;
}

.nav-container {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12.5px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 50px;
    padding: 0.75rem 1.5rem;
}

.nav-list {
    display: flex;
    align-items: center;
    gap: 2rem;
    list-style: none;
}

.nav-link {
    font-family: 'Ubuntu', sans-serif;
    font-weight: bold;
    font-size: 1rem;
    color: rgba(255, 255, 255, 0.7);
    text-decoration: none;
    transition: all 0.3s ease;
    cursor: pointer;
    padding: 0.5rem 1rem;
    border-radius: 20px;
}

.nav-link:hover,
.nav-link.active {
    color: #fff;
    transform: scale(1.1);
}

.nav-link.active {
    background: rgba(255, 255, 255, 0.1);
}

/* Theme Toggle */
.theme-toggle {
    width: 3rem;
    height: 3rem;
    background: rgba(255, 255, 255, 0.1);
    border: none;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    animation: fadeInRight 0.8s ease-out 0.6s both;
}

.theme-toggle:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: scale(1.1);
}

.theme-icon {
    width: 1.5rem;
    height: 1.5rem;
    object-fit: cover;
}

/* Page System */
.page {
    display: none;
    width: 100%;
    min-height: calc(100vh - 120px);
    opacity: 0;
    transform: translateY(50px);
    transition: all 0.6s ease-out;
}

.page.active {
    display: block;
    opacity: 1;
    transform: translateY(0);
}

/* Main Content */
.main-content {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 3rem 1rem;
    text-align: center;
    position: relative;
}

/* Page Specific Styles */
.page-heading {
    font-family: 'Passion One', sans-serif;
    font-weight: normal;
    color: #fff;
    font-size: 3.5rem;
    line-height: 1;
    letter-spacing: 0.05em;
    margin-bottom: 3rem;
    text-shadow: 
        0 0 20px rgba(255, 255, 255, 0.3),
        0 0 40px rgba(52, 41, 211, 0.2);
    animation: fadeInUp 1s ease-out 0.3s both;
}

/* About Page Styles */
.features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
    max-width: 1200px;
    margin: 0 auto;
}

.feature-card {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12.5px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    padding: 2rem;
    text-align: center;
    transition: all 0.3s ease;
    animation: scaleIn 0.6s ease-out forwards;
    animation-delay: calc(var(--delay, 0) * 0.2s);
    opacity: 0;
    transform: scale(0.8);
}

.feature-card:nth-child(1) { --delay: 0; }
.feature-card:nth-child(2) { --delay: 1; }
.feature-card:nth-child(3) { --delay: 2; }
.feature-card:nth-child(4) { --delay: 3; }

.feature-card:hover {
    transform: translateY(-10px) scale(1.02);
    background: rgba(255, 255, 255, 0.08);
    box-shadow: 0 20px 40px rgba(52, 41, 211, 0.2);
}

.feature-icon {
    font-size: 3rem;
    margin-bottom: 1rem;
    animation: iconBounce 2s ease-in-out infinite;
}

.feature-card h3 {
    font-family: 'Russo One', sans-serif;
    font-size: 1.5rem;
    margin-bottom: 1rem;
    color: #fff;
}

.feature-card p {
    font-size: 1rem;
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.8);
}

/* Contact Page Styles */
.contact-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    max-width: 1200px;
    margin: 0 auto;
    align-items: start;
}

.contact-info {
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

.contact-item {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12.5px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 15px;
    padding: 1.5rem;
    transition: all 0.3s ease;
    animation: slideInLeft 0.6s ease-out forwards;
    animation-delay: calc(var(--delay, 0) * 0.2s);
    opacity: 0;
    transform: translateX(-50px);
}

.contact-item:nth-child(1) { --delay: 0; }
.contact-item:nth-child(2) { --delay: 1; }
.contact-item:nth-child(3) { --delay: 2; }

.contact-item:hover {
    transform: translateX(10px);
    background: rgba(255, 255, 255, 0.08);
}

.contact-icon {
    font-size: 2rem;
    margin-bottom: 0.5rem;
}

.contact-item h3 {
    font-family: 'Russo One', sans-serif;
    font-size: 1.2rem;
    margin-bottom: 0.5rem;
    color: #fff;
}

.contact-item p {
    font-size: 1.1rem;
    margin-bottom: 0.25rem;
    color: #fff;
    font-weight: bold;
}

.contact-item span {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.7);
}

.contact-form {
    animation: slideInRight 0.6s ease-out 0.3s both;
}

.security-form {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.form-group {
    position: relative;
}

.form-input,
.form-textarea {
    width: 100%;
    padding: 1rem 1.5rem;
    background: rgba(255, 255, 255, 0.05);
    border: 2px solid rgba(255, 255, 255, 0.1);
    border-radius: 10px;
    color: #fff;
    font-family: 'Ubuntu', sans-serif;
    font-size: 1rem;
    transition: all 0.3s ease;
    outline: none;
}

.form-input:focus,
.form-textarea:focus {
    border-color: #3b82f6;
    background: rgba(255, 255, 255, 0.08);
    box-shadow: 0 0 20px rgba(59, 130, 246, 0.2);
}

.form-input::placeholder,
.form-textarea::placeholder {
    color: rgba(255, 255, 255, 0.5);
}

.submit-button {
    padding: 1rem 2rem;
    background: linear-gradient(90deg, #3b82f6, #9333ea);
    border: none;
    border-radius: 10px;
    color: #fff;
    font-family: 'Ubuntu', sans-serif;
    font-size: 1.1rem;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.submit-button:hover {
    background: linear-gradient(90deg, #2563eb, #7c3aed);
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(52, 41, 211, 0.3);
}

/* Support Page Styles */
.support-content {
    max-width: 1200px;
    margin: 0 auto;
}

.support-categories {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    margin-bottom: 4rem;
}

.support-category {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12.5px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    padding: 2rem;
    text-align: center;
    transition: all 0.3s ease;
    animation: scaleIn 0.6s ease-out forwards;
    animation-delay: calc(var(--delay, 0) * 0.2s);
    opacity: 0;
    transform: scale(0.8);
}

.support-category:nth-child(1) { --delay: 0; }
.support-category:nth-child(2) { --delay: 1; }
.support-category:nth-child(3) { --delay: 2; }

.support-category:hover {
    transform: translateY(-10px) scale(1.02);
    background: rgba(255, 255, 255, 0.08);
}

.category-icon {
    font-size: 3rem;
    margin-bottom: 1rem;
    animation: iconPulse 2s ease-in-out infinite;
}

.support-category h3 {
    font-family: 'Russo One', sans-serif;
    font-size: 1.5rem;
    margin-bottom: 1rem;
    color: #fff;
}

.support-category p {
    font-size: 1rem;
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 1.5rem;
}

.support-button {
    padding: 0.75rem 1.5rem;
    border: none;
    border-radius: 25px;
    color: #fff;
    font-family: 'Ubuntu', sans-serif;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.support-button.emergency {
    background: linear-gradient(90deg, #dc2626, #991b1b);
}

.support-button.technical {
    background: linear-gradient(90deg, #3b82f6, #1d4ed8);
}

.support-button.docs {
    background: linear-gradient(90deg, #059669, #047857);
}

.support-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
}

.support-stats {
    display: flex;
    justify-content: space-around;
    gap: 2rem;
    padding: 2rem;
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(12.5px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    animation: fadeInUp 0.6s ease-out 0.6s both;
}

.stat-item {
    text-align: center;
}

.stat-number {
    font-family: 'Passion One', sans-serif;
    font-size: 2.5rem;
    color: #3b82f6;
    margin-bottom: 0.5rem;
    animation: countUp 2s ease-out 1s both;
}

.stat-label {
    font-family: 'Ubuntu', sans-serif;
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.7);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.content-container {
    max-width: 1200px;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

.main-heading {
    font-family: 'Passion One', sans-serif;
    font-weight: normal;
    color: #fff;
    font-size: 4rem;
    line-height: 1;
    letter-spacing: 0.05em;
    -webkit-text-stroke: 1px #000;
    text-shadow: 
        0 0 20px rgba(255, 255, 255, 0.3),
        0 0 40px rgba(52, 41, 211, 0.2);
    animation: fadeInUp 1s ease-out 0.8s both;
}

.subtitle {
    font-family: 'Passion One', sans-serif;
    font-weight: normal;
    color: #fff;
    font-size: 2rem;
    line-height: 1.2;
    max-width: 800px;
    margin: 0 auto;
    animation: fadeInUp 1s ease-out 1.2s both;
}

.cta-container {
    padding-top: 2rem;
    animation: scaleIn 0.6s ease-out 1.6s both;
}

.cta-button {
    font-family: 'Passion One', sans-serif;
    font-weight: normal;
    font-size: 1.5rem;
    color: #fff;
    background: linear-gradient(90deg, #3b82f6, #9333ea);
    border: 2px solid rgba(255, 255, 255, 0.2);
    padding: 1rem 2rem;
    border-radius: 50px;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
    position: relative;
    overflow: hidden;
}

.cta-button:hover {
    background: linear-gradient(90deg, #2563eb, #7c3aed);
    transform: scale(1.05);
    box-shadow: 0 20px 40px rgba(52, 41, 211, 0.3);
}

.cta-button:active {
    transform: scale(0.95);
}

.cta-text {
    position: relative;
    z-index: 1;
    animation: textGlow 2s ease-in-out infinite;
}

/* Particles */
.particles-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    overflow: hidden;
}

.particle {
    position: absolute;
    width: 8px;
    height: 8px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    animation: particleFloat 5s ease-in-out infinite;
}

/* Mobile navigation styles removed - handled by React component */

/* Responsive Design for New Pages */
@media (max-width: 768px) {
    .contact-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .support-stats {
        flex-direction: column;
        gap: 1rem;
    }

    .features-grid {
        grid-template-columns: 1fr;
    }

    .support-categories {
        grid-template-columns: 1fr;
    }

    .page-heading {
        font-size: 2.5rem;
    }

    .loading-logo-b {
        font-size: 3rem;
    }

    .loading-logo-text span {
        font-size: 1.2rem;
    }

    .loading-bar-container {
        width: 250px;
    }
}

@media (min-width: 640px) {
    .header {
        padding: 2rem 1.5rem;
    }

    .logo-b {
        font-size: 3.5rem;
    }

    .logo-text span {
        font-size: 1.5rem;
    }

    .main-heading {
        font-size: 5rem;
    }

    .subtitle {
        font-size: 2.5rem;
    }

    .cta-button {
        font-size: 1.75rem;
        padding: 1.25rem 2.5rem;
    }

    .content-container {
        gap: 3rem;
    }
}

@media (min-width: 768px) {
    .desktop-nav {
        display: block;
    }

    /* Mobile nav styles removed - React component handles this */

    .main-heading {
        font-size: 6rem;
    }

    .subtitle {
        font-size: 3rem;
    }

    .cta-button {
        font-size: 2rem;
        padding: 1.5rem 3rem;
    }

    .nav-list {
        gap: 2.5rem;
    }

    .nav-link {
        font-size: 1.125rem;
    }
}

@media (min-width: 1024px) {
    .header {
        padding: 2rem 2rem;
    }

    .logo-b {
        font-size: 4rem;
    }

    .logo-text span {
        font-size: 1.75rem;
    }

    .main-heading {
        font-size: 7rem;
    }

    .subtitle {
        font-size: 3.5rem;
    }

    .cta-button {
        font-size: 2.5rem;
        padding: 1.75rem 3.5rem;
    }
}

@media (min-width: 1280px) {
    .main-heading {
        font-size: 9rem;
    }

    .subtitle {
        font-size: 4rem;
    }

    .cta-button {
        font-size: 3rem;
        padding: 2rem 4rem;
    }
}

/* Loading Animations */
@keyframes logoGlow {
    0%, 100% {
        text-shadow: 0 0 20px rgba(59, 130, 246, 0.5);
        filter: drop-shadow(0 0 15px rgba(59, 130, 246, 0.3));
    }
    50% {
        text-shadow: 0 0 40px rgba(147, 51, 234, 0.8);
        filter: drop-shadow(0 0 25px rgba(147, 51, 234, 0.5));
    }
}

@keyframes textShimmer {
    0%, 100% {
        text-shadow: 0 0 10px rgba(255, 255, 255, 0.3);
    }
    50% {
        text-shadow: 0 0 20px rgba(59, 130, 246, 0.6);
    }
}

@keyframes loadingProgress {
    0% {
        width: 0%;
    }
    50% {
        width: 70%;
    }
    100% {
        width: 100%;
    }
}

@keyframes shimmer {
    0% {
        background-position: -200% 0;
    }
    100% {
        background-position: 200% 0;
    }
}

@keyframes textFade {
    0%, 100% {
        opacity: 0.7;
    }
    50% {
        opacity: 1;
    }
}

@keyframes particleRise {
    0% {
        transform: translateY(100vh) scale(0);
        opacity: 0;
    }
    10% {
        opacity: 1;
    }
    90% {
        opacity: 1;
    }
    100% {
        transform: translateY(-100px) scale(1);
        opacity: 0;
    }
}

/* Page Animations */
@keyframes iconBounce {
    0%, 100% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-10px);
    }
}

@keyframes iconPulse {
    0%, 100% {
        transform: scale(1);
        opacity: 1;
    }
    50% {
        transform: scale(1.1);
        opacity: 0.8;
    }
}

@keyframes slideInLeft {
    from {
        opacity: 0;
        transform: translateX(-50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes slideInRight {
    from {
        opacity: 0;
        transform: translateX(50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes countUp {
    from {
        opacity: 0;
        transform: scale(0.5);
    }
    to {
        opacity: 1;
        transform: scale(1);
    }
}

/* Existing Animations */
@keyframes breathe {
    0%, 100% { transform: scale(1) rotate(0deg); }
    50% { transform: scale(1.1) rotate(2deg); }
}

@keyframes float-1 {
    0%, 100% { transform: translate(0, 0); opacity: 0.3; }
    50% { transform: translate(50px, -30px); opacity: 0.6; }
}

@keyframes float-2 {
    0%, 100% { transform: translate(0, 0); opacity: 0.2; }
    50% { transform: translate(-40px, 40px); opacity: 0.5; }
}

@keyframes logoSpin {
    0%, 100% { transform: rotate(180deg); }
    50% { transform: rotate(170deg); }
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(60px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes fadeInLeft {
    from {
        opacity: 0;
        transform: translateX(-60px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes fadeInRight {
    from {
        opacity: 0;
        transform: translateX(60px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes slideInDown {
    from {
        opacity: 0;
        transform: translateY(-100px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes slideInUp {
    from {
        opacity: 0;
        transform: translateY(100px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes scaleIn {
    from {
        opacity: 0;
        transform: scale(0.8);
    }
    to {
        opacity: 1;
        transform: scale(1);
    }
}

@keyframes textGlow {
    0%, 100% {
        text-shadow: 0 0 20px rgba(255, 255, 255, 0.5);
    }
    50% {
        text-shadow: 0 0 30px rgba(52, 41, 211, 0.8);
    }
}

@keyframes particleFloat {
    0% {
        transform: translateY(0) scale(0);
        opacity: 0;
    }
    50% {
        opacity: 1;
        transform: translateY(-100px) scale(1);
    }
    100% {
        transform: translateY(-200px) scale(0);
        opacity: 0;
    }
}

/* Security Importance Section */
.security-section {
    padding: 4rem 0;
    margin-top: 3rem;
    background: linear-gradient(135deg, rgba(220, 38, 38, 0.1), rgba(147, 51, 234, 0.1));
    border-radius: 30px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    position: relative;
    overflow: hidden;
}

.security-content {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: center;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
    text-align: center;
}

.security-icon-container {
    display: flex !important;
    justify-content: center !important;
    align-items: center !important;
    position: relative !important;
    height: 200px !important;
    width: 100% !important;
    margin: 0 auto !important;
}

.cyber-shield {
    position: relative !important;
    display: flex !important;
    align-items: center !important;
    justify-content: center !important;
    margin: 0 auto !important;
    width: 120px !important;
    height: 120px !important;
    animation: shieldPulseFixed 3s ease-in-out infinite;
}

.shield-layers {
    position: absolute;
    width: 100%;
    height: 100%;
}

.shield-layer {
    position: absolute;
    border: 2px solid;
    border-radius: 50%;
    animation: shieldGlow 2s ease-in-out infinite alternate;
}

.layer-1 {
    width: 120px;
    height: 120px;
    border-color: rgba(59, 130, 246, 0.6);
    animation-duration: 8s;
}

.layer-2 {
    width: 90px;
    height: 90px;
    border-color: rgba(147, 51, 234, 0.6);
    top: 15px;
    left: 15px;
    animation-duration: 6s;
    animation-direction: reverse;
}

.layer-3 {
    width: 60px;
    height: 60px;
    border-color: rgba(220, 38, 38, 0.6);
    top: 30px;
    left: 30px;
    animation-duration: 4s;
}

.shield-core {
    font-size: 3rem;
    z-index: 10;
    animation: shieldGlow 2s ease-in-out infinite;
}

.cyber-particles {
    position: absolute;
    width: 100%;
    height: 100%;
    pointer-events: none;
}

.security-text {
    text-align: center;
}

.security-heading {
    font-family: 'Passion One', sans-serif;
    font-size: 2.5rem;
    color: #fff;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(220, 38, 38, 0.5);
    animation: textFlicker 4s ease-in-out infinite;
}

.security-description {
    font-size: 1.2rem;
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.9);
    margin-bottom: 2rem;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
}

.highlight-text {
    color: #ef4444;
    font-weight: bold;
    text-shadow: 0 0 10px rgba(239, 68, 68, 0.8);
    animation: dangerGlow 2s ease-in-out infinite;
}

.cyber-threat {
    color: #3b82f6;
    font-weight: bold;
    text-shadow: 0 0 10px rgba(59, 130, 246, 0.8);
    animation: protectionGlow 2s ease-in-out infinite 1s;
}

.threat-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: 2rem;
    margin-top: 2rem;
}

.stat-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 1rem;
    background: rgba(255, 255, 255, 0.05);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(5px);
    transition: transform 0.3s ease;
}

.stat-item:hover {
    transform: translateY(-5px);
}

.stat-number {
    font-family: 'Rubik Mono One', monospace;
    font-size: 1.5rem;
    color: #ef4444;
    text-shadow: 0 0 10px rgba(239, 68, 68, 0.5);
    animation: numberPulse 3s ease-in-out infinite;
}

.stat-label {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.7);
    margin-top: 0.5rem;
}

/* Breach Check Section */
.breach-check-section {
    padding: 4rem 0;
    margin-top: 3rem;
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(16, 185, 129, 0.1));
    border-radius: 30px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    position: relative;
}

.breach-check-content {
    max-width: 800px;
    margin: 0 auto;
    padding: 0 2rem;
    text-align: center;
}

.check-heading {
    font-family: 'Passion One', sans-serif;
    font-size: 2.5rem;
    color: #fff;
    margin-bottom: 1rem;
    text-shadow: 0 0 20px rgba(59, 130, 246, 0.5);
}

.check-description {
    font-size: 1.1rem;
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 3rem;
    line-height: 1.6;
}

.breach-check-form {
    margin-bottom: 3rem;
}

.input-group {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.input-container {
    position: relative;
    display: flex;
    align-items: center;
}

.breach-input {
    width: 100%;
    padding: 1rem 3rem 1rem 1rem;
    background: rgba(255, 255, 255, 0.05);
    border: 2px solid rgba(255, 255, 255, 0.1);
    border-radius: 15px;
    color: #fff;
    font-size: 1rem;
    transition: all 0.3s ease;
    backdrop-filter: blur(5px);
}

.breach-input:focus {
    outline: none;
    border-color: rgba(59, 130, 246, 0.6);
    box-shadow: 0 0 20px rgba(59, 130, 246, 0.3);
    background: rgba(255, 255, 255, 0.1);
}

.breach-input::placeholder {
    color: rgba(255, 255, 255, 0.5);
}

.input-icon {
    position: absolute;
    right: 1rem;
    font-size: 1.2rem;
    opacity: 0.7;
    animation: iconFloat 3s ease-in-out infinite;
}

.check-button {
    position: relative;
    padding: 1.2rem 3rem;
    background: linear-gradient(135deg, #3b82f6, #10b981);
    border: none;
    border-radius: 20px;
    color: #fff;
    font-family: 'Passion One', sans-serif;
    font-size: 1.3rem;
    cursor: pointer;
    transition: all 0.3s ease;
    overflow: hidden;
    box-shadow: 0 10px 30px rgba(59, 130, 246, 0.3);
    margin-bottom: 2rem;
}

.check-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 15px 40px rgba(59, 130, 246, 0.4);
}

.check-button:active {
    transform: translateY(0);
}

.check-button.scanning {
    background: linear-gradient(135deg, #fbbf24, #f59e0b);
    animation: scanningPulse 1.5s ease-in-out infinite;
}

.scan-animation {
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transition: left 0.6s ease;
}

.check-button.scanning .scan-animation {
    animation: scanMove 1.5s linear infinite;
}

.scan-line {
    width: 100%;
    height: 2px;
    background: rgba(255, 255, 255, 0.6);
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
}

.breach-results {
    min-height: 100px;
    background: rgba(0, 0, 0, 0.3);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    padding: 2rem;
    text-align: center;
    color: rgba(255, 255, 255, 0.7);
    display: none;
}

.breach-results.show {
    display: block;
    animation: fadeInUp 0.5s ease-out;
}

.security-badges {
    display: flex;
    justify-content: center;
    gap: 2rem;
    flex-wrap: wrap;
}

.badge {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1.5rem;
    background: rgba(255, 255, 255, 0.05);
    border-radius: 25px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.8);
    transition: transform 0.3s ease;
}

.badge:hover {
    transform: translateY(-2px);
}

.badge-icon {
    font-size: 1.1rem;
}

/* Responsive Design for New Sections */
@media (min-width: 768px) {
    .security-content {
        grid-template-columns: 1fr 1.5fr;
        text-align: left;
    }

    .security-text {
        text-align: left;
    }

    .input-group {
        grid-template-columns: 1fr 1fr;
    }

    .security-heading,
    .check-heading {
        font-size: 3rem;
    }

    .security-heading {
        margin-left: 0;
        text-align: left;
    }

    .security-description {
        margin-left: 0;
        text-align: left;
    }
}

@media (min-width: 1024px) {
    .security-heading,
    .check-heading {
        font-size: 3.5rem;
    }

    .threat-stats {
        grid-template-columns: repeat(3, 1fr);
    }
}

/* Animations for New Sections */
@keyframes shieldPulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
}

@keyframes shieldPulseFixed {
    0%, 100% { 
        transform: scale(1);
        filter: brightness(1) drop-shadow(0 0 5px rgba(59, 130, 246, 0.3));
    }
    50% { 
        transform: scale(1.1);
        filter: brightness(1.2) drop-shadow(0 0 15px rgba(59, 130, 246, 0.6));
    }
}

@keyframes shieldRotate {
    from { transform: scale(1); }
    to { transform: scale(1); }
}

@keyframes shieldGlow {
    0%, 100% { 
        text-shadow: 0 0 20px rgba(59, 130, 246, 0.8);
        filter: drop-shadow(0 0 10px rgba(59, 130, 246, 0.6));
    }
    50% { 
        text-shadow: 0 0 30px rgba(147, 51, 234, 0.8);
        filter: drop-shadow(0 0 15px rgba(147, 51, 234, 0.6));
    }
}

@keyframes textFlicker {
    0%, 100% { opacity: 1; }
    95% { opacity: 1; }
    96% { opacity: 0.8; }
    97% { opacity: 1; }
    98% { opacity: 0.9; }
    99% { opacity: 1; }
}

@keyframes dangerGlow {
    0%, 100% { 
        color: #ef4444;
        text-shadow: 0 0 10px rgba(239, 68, 68, 0.8);
    }
    50% { 
        color: #dc2626;
        text-shadow: 0 0 20px rgba(220, 38, 38, 1);
    }
}

@keyframes protectionGlow {
    0%, 100% { 
        color: #3b82f6;
        text-shadow: 0 0 10px rgba(59, 130, 246, 0.8);
    }
    50% { 
        color: #2563eb;
        text-shadow: 0 0 20px rgba(37, 99, 235, 1);
    }
}

@keyframes numberPulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
}

@keyframes iconFloat {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-3px); }
}

@keyframes scanningPulse {
    0%, 100% { box-shadow: 0 10px 30px rgba(59, 130, 246, 0.3); }
    50% { box-shadow: 0 15px 40px rgba(251, 191, 36, 0.5); }
}

@keyframes scanMove {
    0% { left: -100%; }
    100% { left: 100%; }
}

/* Loading animations - applied by JavaScript */
.fade-in {
    animation: fadeInUp 0.8s ease-out forwards;
}

.slide-in-left {
    animation: fadeInLeft 0.8s ease-out forwards;
}

.slide-in-right {
    animation: fadeInRight 0.8s ease-out forwards;
}

.scale-in {
    animation: scaleIn 0.6s ease-out forwards;
}

/* Password Generator Section */
.password-generator-section {
    position: relative;
    margin: 4rem 0;
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.95), rgba(15, 23, 42, 0.98));
    border: 2px solid rgba(59, 130, 246, 0.3);
    border-radius: 25px;
    overflow: hidden;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.5);
}

.generator-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    overflow: hidden;
}

.matrix-rain {
    position: absolute;
    width: 100%;
    height: 100%;
    background: radial-gradient(2px 2px at 25px 35px, rgba(0, 255, 0, 0.3), transparent),
                radial-gradient(2px 2px at 75px 85px, rgba(0, 255, 255, 0.2), transparent),
                radial-gradient(1px 1px at 125px 45px, rgba(0, 200, 255, 0.4), transparent);
    background-size: 150px 200px;
    animation: matrixRain 8s linear infinite;
}

.cyber-grid {
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: 
        linear-gradient(rgba(0, 255, 255, 0.1) 1px, transparent 1px),
        linear-gradient(90deg, rgba(0, 255, 255, 0.1) 1px, transparent 1px);
    background-size: 60px 60px;
    animation: gridShift 15s linear infinite;
    opacity: 0.3;
}

.security-beams {
    position: absolute;
    width: 100%;
    height: 100%;
}

.beam {
    position: absolute;
    background: linear-gradient(90deg, transparent, rgba(0, 255, 255, 0.6), transparent);
    height: 2px;
    width: 100%;
    animation: beamScan 6s ease-in-out infinite;
}

.beam-1 {
    top: 20%;
    animation-delay: 0s;
}

.beam-2 {
    top: 50%;
    animation-delay: 2s;
}

.beam-3 {
    top: 80%;
    animation-delay: 4s;
}

.generator-content {
    position: relative;
    z-index: 10;
    padding: 3rem;
}

.generator-container {
    max-width: 1000px;
    margin: 0 auto;
}

/* Generator Header */
.generator-header {
    text-align: center;
    margin-bottom: 3rem;
}

.header-icon-container {
    display: flex;
    justify-content: center;
    margin-bottom: 2rem;
}

.lock-animation {
    position: relative;
    display: inline-block;
}

.lock-body {
    font-size: 4rem;
    animation: lockPulse 3s ease-in-out infinite;
    filter: drop-shadow(0 0 20px rgba(0, 255, 255, 0.8));
}

.lock-waves {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.wave {
    position: absolute;
    border: 2px solid rgba(0, 255, 255, 0.3);
    border-radius: 50%;
    animation: waveExpand 3s ease-out infinite;
}

.wave-1 {
    width: 80px;
    height: 80px;
    margin: -40px;
    animation-delay: 0s;
}

.wave-2 {
    width: 120px;
    height: 120px;
    margin: -60px;
    animation-delay: 1s;
}

.wave-3 {
    width: 160px;
    height: 160px;
    margin: -80px;
    animation-delay: 2s;
}

.generator-title {
    font-family: 'Passion One', sans-serif;
    font-size: 3rem;
    color: #fff;
    margin-bottom: 1rem;
    text-shadow: 0 0 30px rgba(0, 255, 255, 0.8);
    animation: titleGlow 4s ease-in-out infinite;
}

.generator-subtitle {
    font-size: 1.2rem;
    color: rgba(255, 255, 255, 0.8);
    margin-bottom: 2rem;
}

/* Generator Main */
.generator-main {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: start;
}

/* Password Options */
.password-options {
    background: rgba(0, 0, 0, 0.4);
    border: 1px solid rgba(0, 255, 255, 0.3);
    border-radius: 20px;
    padding: 2rem;
    backdrop-filter: blur(10px);
}

.option-group {
    margin-bottom: 2rem;
}

.option-label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-family: 'Passion One', sans-serif;
    font-size: 1.3rem;
    color: #00ffff;
    margin-bottom: 1rem;
    text-shadow: 0 0 10px rgba(0, 255, 255, 0.6);
}

.label-icon {
    font-size: 1.2rem;
    animation: iconSpin 8s linear infinite;
}

.length-controls {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.length-slider {
    flex: 1;
    height: 8px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 4px;
    outline: none;
    appearance: none;
    cursor: pointer;
}

.length-slider::-webkit-slider-thumb {
    appearance: none;
    width: 20px;
    height: 20px;
    background: linear-gradient(135deg, #00ffff, #0080ff);
    border-radius: 50%;
    cursor: pointer;
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.8);
    animation: thumbGlow 2s ease-in-out infinite;
}

.length-slider::-moz-range-thumb {
    width: 20px;
    height: 20px;
    background: linear-gradient(135deg, #00ffff, #0080ff);
    border-radius: 50%;
    cursor: pointer;
    border: none;
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.8);
}

.length-display {
    display: flex;
    flex-direction: column;
    align-items: center;
    min-width: 80px;
    padding: 0.5rem;
    background: rgba(0, 255, 255, 0.1);
    border: 1px solid rgba(0, 255, 255, 0.3);
    border-radius: 10px;
}

.length-display #length-value {
    font-family: 'Rubik Mono One', monospace;
    font-size: 1.5rem;
    color: #00ffff;
    text-shadow: 0 0 10px rgba(0, 255, 255, 0.8);
}

.length-unit {
    font-size: 0.8rem;
    color: rgba(255, 255, 255, 0.7);
}

.options-grid {
    display: grid;
    gap: 1rem;
}

.option-item {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 15px;
    transition: all 0.3s ease;
}

.option-item:hover {
    background: rgba(0, 255, 255, 0.05);
    border-color: rgba(0, 255, 255, 0.3);
    transform: translateX(5px);
}

.toggle-switch {
    position: relative;
    width: 60px;
    height: 30px;
}

.toggle-input {
    opacity: 0;
    width: 0;
    height: 0;
}

.toggle-label {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(255, 255, 255, 0.1);
    border: 2px solid rgba(255, 255, 255, 0.2);
    border-radius: 30px;
    transition: all 0.3s ease;
}

.toggle-slider {
    position: absolute;
    height: 22px;
    width: 22px;
    left: 2px;
    bottom: 2px;
    background: #666;
    border-radius: 50%;
    transition: all 0.3s ease;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
}

.toggle-input:checked + .toggle-label {
    background: rgba(0, 255, 255, 0.2);
    border-color: rgba(0, 255, 255, 0.6);
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.4);
}

.toggle-input:checked + .toggle-label .toggle-slider {
    transform: translateX(30px);
    background: #00ffff;
    box-shadow: 0 0 15px rgba(0, 255, 255, 0.8);
}

.option-info {
    flex: 1;
}

.option-name {
    display: block;
    color: #fff;
    font-weight: bold;
    margin-bottom: 0.2rem;
}

.option-example {
    display: block;
    color: rgba(255, 255, 255, 0.6);
    font-size: 0.9rem;
    font-family: 'Rubik Mono One', monospace;
}

/* Password Output */
.password-output {
    background: rgba(0, 0, 0, 0.6);
    border: 1px solid rgba(0, 255, 255, 0.3);
    border-radius: 20px;
    padding: 2rem;
    backdrop-filter: blur(15px);
}

.output-container {
    margin-bottom: 2rem;
}

.password-display {
    position: relative;
    margin-bottom: 1.5rem;
}

.password-field {
    width: 100%;
    padding: 1.5rem;
    background: rgba(0, 0, 0, 0.8);
    border: 2px solid rgba(0, 255, 255, 0.3);
    border-radius: 15px;
    color: #00ffff;
    font-family: 'Rubik Mono One', monospace;
    font-size: 1.2rem;
    text-align: center;
    letter-spacing: 2px;
    word-break: break-all;
    transition: all 0.3s ease;
}

.password-field:focus {
    outline: none;
    border-color: rgba(0, 255, 255, 0.8);
    box-shadow: 0 0 25px rgba(0, 255, 255, 0.5);
    text-shadow: 0 0 10px rgba(0, 255, 255, 0.8);
}

.password-field.generated {
    animation: passwordReveal 0.8s ease-out;
}

.field-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(90deg, transparent, rgba(0, 255, 255, 0.1), transparent);
    border-radius: 15px;
    opacity: 0;
    animation: fieldScan 2s ease-in-out infinite;
}

.password-actions {
    display: flex;
    gap: 1rem;
}

.generate-btn {
    flex: 1;
    position: relative;
    padding: 1.2rem 2rem;
    background: linear-gradient(135deg, #00ffff, #0080ff);
    border: none;
    border-radius: 15px;
    color: #000;
    font-family: 'Passion One', sans-serif;
    font-size: 1.3rem;
    font-weight: bold;
    cursor: pointer;
    transition: all 0.3s ease;
    overflow: hidden;
    box-shadow: 0 10px 30px rgba(0, 255, 255, 0.3);
}

.generate-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 40px rgba(0, 255, 255, 0.5);
}

.generate-btn:active {
    transform: translateY(0);
}

.generate-btn.generating {
    background: linear-gradient(135deg, #ffaa00, #ff6600);
    animation: generatePulse 1s ease-in-out infinite;
}

.btn-loading {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    opacity: 0;
    transition: opacity 0.3s ease;
}

.generate-btn.generating .btn-loading {
    opacity: 1;
}

.generate-btn.generating .btn-text {
    opacity: 0;
}

.loading-dots {
    display: flex;
    gap: 0.3rem;
}

.loading-dots span {
    width: 8px;
    height: 8px;
    background: #000;
    border-radius: 50%;
    animation: dotBounce 1.4s ease-in-out infinite both;
}

.loading-dots span:nth-child(1) { animation-delay: -0.32s; }
.loading-dots span:nth-child(2) { animation-delay: -0.16s; }
.loading-dots span:nth-child(3) { animation-delay: 0s; }

.btn-cyber-effect {
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
    transition: left 0.6s ease;
}

.generate-btn:hover .btn-cyber-effect {
    left: 100%;
}

.copy-btn {
    padding: 1.2rem;
    background: rgba(255, 255, 255, 0.1);
    border: 2px solid rgba(255, 255, 255, 0.2);
    border-radius: 15px;
    color: #fff;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.copy-btn:hover:not(:disabled) {
    background: rgba(0, 255, 0, 0.2);
    border-color: rgba(0, 255, 0, 0.5);
    transform: translateY(-2px);
}

.copy-btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

.copy-btn.copied {
    background: rgba(0, 255, 0, 0.3);
    border-color: rgba(0, 255, 0, 0.6);
    animation: copySuccess 0.6s ease-out;
}

/* Strength Meter */
.strength-meter {
    margin-top: 1.5rem;
}

.strength-label {
    color: #fff;
    margin-bottom: 0.5rem;
    font-weight: bold;
}

.strength-bar {
    position: relative;
    height: 12px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 6px;
    overflow: hidden;
    margin-bottom: 0.5rem;
}

.strength-fill {
    height: 100%;
    width: 0%;
    background: linear-gradient(90deg, #ff4444, #ffaa00, #00ff00);
    border-radius: 6px;
    transition: all 0.8s ease;
    position: relative;
}

.strength-fill::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    animation: strengthScan 2s linear infinite;
}

.strength-indicators {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: space-between;
    padding: 0 2px;
}

.indicator {
    width: 2px;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    border-radius: 1px;
}

.strength-text {
    color: rgba(255, 255, 255, 0.8);
    font-size: 0.9rem;
    text-align: center;
}

/* Generator Footer */
.generator-footer {
    margin-top: 3rem;
    padding-top: 2rem;
    border-top: 1px solid rgba(0, 255, 255, 0.2);
}

.security-tips {
    display: flex;
    justify-content: center;
    gap: 2rem;
    flex-wrap: wrap;
}

.tip-item {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1.5rem;
    background: rgba(0, 255, 255, 0.05);
    border: 1px solid rgba(0, 255, 255, 0.2);
    border-radius: 25px;
    color: rgba(255, 255, 255, 0.8);
    font-size: 0.9rem;
    transition: all 0.3s ease;
}

.tip-item:hover {
    background: rgba(0, 255, 255, 0.1);
    border-color: rgba(0, 255, 255, 0.4);
    transform: translateY(-2px);
}

.tip-icon {
    font-size: 1.1rem;
    animation: tipIconFloat 3s ease-in-out infinite;
}

/* Responsive Design */
@media (max-width: 1024px) {
    .generator-main {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .generator-title {
        font-size: 2.5rem;
    }
}

@media (max-width: 768px) {
    .generator-content {
        padding: 2rem 1rem;
    }

    .generator-title {
        font-size: 2rem;
    }

    .password-actions {
        flex-direction: column;
    }

    .security-tips {
        flex-direction: column;
        align-items: center;
    }
}

/* Cyber Footer */
.cyber-footer {
    position: relative;
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.95), rgba(15, 23, 42, 0.98));
    border-top: 2px solid rgba(59, 130, 246, 0.3);
    margin-top: 4rem;
    overflow: hidden;
}

.footer-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
}

.footer-grid {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        linear-gradient(rgba(59, 130, 246, 0.1) 1px, transparent 1px),
        linear-gradient(90deg, rgba(59, 130, 246, 0.1) 1px, transparent 1px);
    background-size: 50px 50px;
    animation: gridMove 20s linear infinite;
}

.footer-particles {
    position: absolute;
    width: 100%;
    height: 100%;
    background: radial-gradient(2px 2px at 20px 30px, rgba(59, 130, 246, 0.3), transparent),
                radial-gradient(2px 2px at 40px 70px, rgba(147, 51, 234, 0.3), transparent),
                radial-gradient(1px 1px at 90px 40px, rgba(16, 185, 129, 0.4), transparent);
    background-size: 100px 100px;
    animation: particleFloat 15s ease-in-out infinite;
}

.cyber-lines {
    position: absolute;
    width: 100%;
    height: 100%;
}

.line {
    position: absolute;
    background: linear-gradient(90deg, transparent, rgba(59, 130, 246, 0.6), transparent);
    height: 1px;
    animation: lineFlow 8s ease-in-out infinite;
}

.line-1 {
    top: 20%;
    width: 60%;
    left: -60%;
    animation-delay: 0s;
}

.line-2 {
    top: 60%;
    width: 80%;
    right: -80%;
    animation-delay: 3s;
    animation-direction: reverse;
}

.line-3 {
    top: 80%;
    width: 40%;
    left: -40%;
    animation-delay: 6s;
}

.footer-content {
    position: relative;
    z-index: 10;
    padding: 4rem 0 0;
}

.footer-container {
    max-width: 1280px;
    margin: 0 auto;
    padding: 0 2rem;
}

/* Footer Brand Section */
.footer-brand {
    text-align: center;
    margin-bottom: 4rem;
    position: relative;
}

.footer-logo-container {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 1rem;
    margin-bottom: 1.5rem;
    position: relative;
}

.footer-logo-b {
    font-family: 'Rubik Mono One', monospace;
    font-size: 4rem;
    color: #fff;
    transform: rotate(180deg);
    animation: footerLogoSpin 6s ease-in-out infinite;
