/* Footer Links Grid */
.footer-links-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 3rem;
    margin-bottom: 4rem;
}

.footer-section {
    text-align: left;
}

.section-title {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-family: 'Passion One', sans-serif;
    font-size: 1.3rem;
    color: #fff;
    margin-bottom: 1.5rem;
    text-shadow: 0 0 10px rgba(59, 130, 246, 0.5);
}

.title-icon {
    font-size: 1.2rem;
