/* Privacy Policy Page Styles */
.privacy-background {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
}

.data-streams {
    position: absolute;
    width: 100%;
    height: 100%;
    overflow: hidden;
}

.privacy-stream-1,
.privacy-stream-2,
.privacy-stream-3 {
    position: absolute;
    width: 2px;
    height: 100px;
    background: linear-gradient(to bottom, transparent, rgba(59, 130, 246, 0.6), transparent);
    animation: privacyStreamFlow 8s linear infinite;
}

.privacy-stream-1 {
    left: 20%;
    animation-delay: 0s;
}

.privacy-stream-2 {
    left: 60%;
    animation-delay: 2s;
}

.privacy-stream-3 {
    left: 80%;
    animation-delay: 4s;
}

.security-mesh {
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: 
        linear-gradient(rgba(59, 130, 246, 0.1) 1px, transparent 1px),
        linear-gradient(90deg, rgba(59, 130, 246, 0.1) 1px, transparent 1px);
    background-size: 50px 50px;
    animation: meshShift 20s linear infinite;
}

.floating-shields {
