}

/* Terms of Service Page Styles */
.terms-background {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
}

.terms-stream-1,
.terms-stream-2,
.terms-stream-3 {
    position: absolute;
    width: 2px;
    height: 100px;
    background: linear-gradient(to bottom, transparent, rgba(147, 51, 234, 0.6), transparent);
    animation: privacyStreamFlow 8s linear infinite;
}

.terms-stream-1 {
    left: 15%;
    animation-delay: 1s;
}

.terms-stream-2 {
    left: 55%;
