        transform: translate(-50%, -50%) scale(1.2);
        opacity: 0.6;
    }
}

@keyframes taglineGlow {
    0%, 100% {
        color: #3b82f6;
        text-shadow: 0 0 10px rgba(59, 130, 246, 0.8);
    }
    50% {
        color: #2563eb;
        text-shadow: 0 0 20px rgba(37, 99, 235, 1);
    }
}

@keyframes indicatorPulse {
    0%, 100% {
        opacity: 1;
        transform: scale(1);
    }
    50% {
        opacity: 0.6;
        transform: scale(1.2);
    }
}

@keyframes emergencyBlink {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

@keyframes hotlineGlow {
    0%, 100% {
        color: #ef4444;
        text-shadow: 0 0 10px rgba(239, 68, 68, 0.6);
    }
    50% {
        color: #dc2626;
        text-shadow: 0 0 20px rgba(220, 38, 38, 0.8);
    }
}

@keyframes btnIconSpark {
    0%, 100% { transform: scale(1) rotate(0deg); }
    50% { transform: scale(1.2) rotate(180deg); }
}

@keyframes certGlow {
    0%, 100% {
        border-color: rgba(255, 255, 255, 0.2);
        box-shadow: none;
    }
    50% {
        border-color: rgba(59, 130, 246, 0.5);
        box-shadow: 0 0 15px rgba(59, 130, 246, 0.3);
    }
}

@keyframes footerParticleFloat {
    0% {
        transform: translateY(0) rotate(0deg);
        opacity: 0;
    }
    10% {
        opacity: 1;
    }
    90% {
        opacity: 1;
    }
    100% {
        transform: translateY(-300px) rotate(360deg);
        opacity: 0;
    }
}

@keyframes feedbackSlide {
    from {
        opacity: 0;
        transform: translateX(-50%) translateY(-10px);
    }
    to {
        opacity: 1;
        transform: translateX(-50%) translateY(0);
    }
}

@keyframes linkRipple {
    0% {
        transform: translateX(-100%);
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    100% {
        transform: translateX(100%);
        opacity: 0;
    }
}

@keyframes emergencyPulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
}

.indicator.animate-in {
    animation: indicatorSlideIn 0.6s ease-out forwards;
}

@keyframes indicatorSlideIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Password Generator Animations */
@keyframes matrixRain {
    0% { transform: translateY(0); }
    100% { transform: translateY(100px); }
}

@keyframes gridShift {
    0% { transform: translate(0, 0); }
    100% { transform: translate(60px, 60px); }
}

@keyframes beamScan {
    0% { transform: translateX(-100%); opacity: 0; }
    50% { opacity: 1; }
    100% { transform: translateX(100%); opacity: 0; }
}

@keyframes lockPulse {
    0%, 100% {
        transform: scale(1);
        filter: drop-shadow(0 0 20px rgba(0, 255, 255, 0.8));
    }
    50% {
        transform: scale(1.1);
        filter: drop-shadow(0 0 30px rgba(0, 255, 255, 1));
    }
}

@keyframes waveExpand {
    0% {
        transform: scale(0);
        opacity: 1;
    }
    100% {
        transform: scale(1);
        opacity: 0;
    }
}

@keyframes titleGlow {
    0%, 100% {
        text-shadow: 0 0 30px rgba(0, 255, 255, 0.8);
    }
    50% {
        text-shadow: 0 0 40px rgba(0, 255, 255, 1), 0 0 60px rgba(0, 150, 255, 0.8);
    }
}

@keyframes iconSpin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

@keyframes thumbGlow {
    0%, 100% {
        box-shadow: 0 0 15px rgba(0, 255, 255, 0.8);
    }
    50% {
        box-shadow: 0 0 25px rgba(0, 255, 255, 1);
    }
}

@keyframes passwordReveal {
    0% {
        transform: scale(0.9);
        opacity: 0;
    }
    50% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

@keyframes fieldScan {
    0% { opacity: 0; }
    50% { opacity: 1; }
    100% { opacity: 0; }
}

@keyframes generatePulse {
    0%, 100% {
        box-shadow: 0 10px 30px rgba(255, 170, 0, 0.3);
    }
    50% {
        box-shadow: 0 15px 40px rgba(255, 170, 0, 0.6);
    }
}

@keyframes dotBounce {
    0%, 80%, 100% {
        transform: scale(0);
    }
    40% {
        transform: scale(1);
    }
}

@keyframes copySuccess {
    0% { transform: scale(1); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
}

@keyframes strengthScan {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

@keyframes tipIconFloat {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-3px); }
}

/* New Animations */
@keyframes transitionIn {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

@keyframes dotPulse {
    0%, 100% {
        transform: scale(1);
        opacity: 1;
    }
    50% {
        transform: scale(1.5);
        opacity: 0.7;
    }
}

@keyframes fadeOut {
    from {
        opacity: 1;
        transform: translateY(0);
    }
    to {
        opacity: 0;
        transform: translateY(-20px);
    }
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes profileGlow {
    0%, 100% {
        box-shadow: 0 0 20px rgba(59, 130, 246, 0.5);
    }
    50% {
        box-shadow: 0 0 30px rgba(0, 255, 255, 0.7);
    }
}

@keyframes badgeFloat {
    0%, 100% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-3px);
    }
}

@keyframes nameGlow {
    0%, 100% {
        text-shadow: 0 0 20px rgba(0, 255, 255, 0.6);
    }
    50% {
        text-shadow: 0 0 30px rgba(59, 130, 246, 0.8);
    }
}

@keyframes initialSpin {
    0% { transform: scale(1); }
    100% { transform: scale(1); }
}

@keyframes initialTextPulse {
    0%, 100% {
        opacity: 1;
        transform: scale(1);
    }
    50% {
        opacity: 0.7;
        transform: scale(1.05);
    }
}

/* Dynamic Text Animation */
@keyframes typeWriter {
    from { width: 0; }
    to { width: 100%; }
}

@keyframes blinkCursor {
    from, to { border-color: transparent; }
    50% { border-color: #3b82f6; }
}

.typing-text {
    overflow: hidden;
    white-space: nowrap;
    margin: 0 auto;
}

/* Dynamic Counter Animation */
.counter {
    font-family: 'Rubik Mono One', monospace;
    font-size: 2rem;
    color: #00ffff;
    animation: counterGlow 2s ease-in-out infinite;
}

@keyframes counterGlow {
    0%, 100% {
        text-shadow: 0 0 10px rgba(0, 255, 255, 0.8);
    }
    50% {
        text-shadow: 0 0 20px rgba(0, 255, 255, 1), 0 0 30px rgba(59, 130, 246, 0.8);
    }
}

/* Floating Elements */
.floating-element {
    animation: floatUpDown 6s ease-in-out infinite;
}

@keyframes floatUpDown {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
}

/* Pulse Effect */
.pulse-effect {
    animation: pulseGlow 3s ease-in-out infinite;
}

@keyframes pulseGlow {
    0%, 100% {
        transform: scale(1);
        box-shadow: 0 0 20px rgba(59, 130, 246, 0.5);
    }
    50% {
        transform: scale(1.05);
        box-shadow: 0 0 30px rgba(59, 130, 246, 0.8), 0 0 40px rgba(0, 255, 255, 0.6);
    }
}

/* Interactive Hover Effects */
.interactive-card {
    transition: all 0.3s ease;
    cursor: pointer;
}

.interactive-card:hover {
    transform: translateY(-10px) rotateX(5deg);
    box-shadow: 0 25px 50px rgba(59, 130, 246, 0.3);
}

/* Light Mode Adjustments */
.light-mode .background-container {
    background: linear-gradient(135deg, #f1f5f9, #e2e8f0);
}

.light-mode .hero-gradient {
    background: radial-gradient(
        ellipse at center,
        rgba(59, 130, 246, 0.2) 0%,
        rgba(147, 51, 234, 0.1) 50%,
        transparent 100%
    );
}

.light-mode .floating-orb {
    opacity: 0.6;
}

.light-mode .header {
    backdrop-filter: blur(10px);
}

.light-mode .nav-container,
.light-mode .mobile-nav-container {
    background: var(--glass-bg);
    border-color: var(--border-color);
}

.light-mode .security-section,
.light-mode .breach-check-section,
.light-mode .cyber-footer {
    background: linear-gradient(135deg, var(--glass-bg), var(--bg-secondary));
    border-color: var(--border-color);
    box-shadow: var(--light-shadow-lg);
}

/* Light mode password generator specific styles */
.light-mode .password-generator-section {
    background: linear-gradient(135deg, var(--light-card-bg), var(--bg-secondary));
    border: 1px solid var(--border-color);
    color: var(--text-primary);
    box-shadow: var(--light-shadow-lg);
}

.light-mode .generator-title {
    color: var(--text-primary);
    text-shadow: none;
}

.light-mode .generator-subtitle {
    color: var(--text-secondary);
}

.light-mode .password-options,
.light-mode .password-output {
    background: var(--light-card-bg);
    border: 1px solid var(--border-color);
    box-shadow: var(--light-shadow);
}

.light-mode .option-item {
    background: var(--light-card-bg);
    border: 1px solid var(--border-color);
    color: var(--text-primary);
}

.light-mode .option-item:hover {
    background: var(--light-accent-bg);
    border-color: var(--accent-primary);
}

.light-mode .option-name {
    color: var(--text-primary);
    font-weight: 600;
}

.light-mode .option-example {
    color: var(--text-secondary);
}

.light-mode .password-field {
    background: var(--light-input-bg);
    border: 2px solid var(--border-color);
    color: var(--text-primary);
}

.light-mode .password-field:focus {
    border-color: var(--accent-primary);
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    background: rgba(255, 255, 255, 0.95);
}

.light-mode .generate-btn {
    background: linear-gradient(135deg, var(--accent-primary), var(--accent-secondary));
    color: #ffffff;
    box-shadow: var(--light-shadow);
}

.light-mode .generate-btn:hover {
    background: linear-gradient(135deg, #1d4ed8, #0284c7);
    box-shadow: var(--light-shadow-lg);
}

.light-mode .copy-btn {
    background: var(--light-card-bg);
    border: 2px solid var(--border-color);
    color: var(--text-primary);
}

.light-mode .copy-btn:hover:not(:disabled) {
    background: var(--light-accent-bg);
    border-color: var(--accent-primary);
    color: var(--accent-primary);
}

.light-mode .copy-btn.copied {
    background: rgba(22, 163, 74, 0.1);
    border-color: var(--light-success);
    color: var(--light-success);
}

.light-mode .strength-bar {
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
}

.light-mode .strength-text {
    color: var(--text-primary);
    font-weight: 600;
}

.light-mode .indicator {
    background: var(--bg-secondary);
}

.light-mode .indicator.active {
    background: var(--light-success);
}

.light-mode .length-display {
    background: var(--light-accent-bg);
    border: 1px solid var(--accent-primary);
}

.light-mode #length-value {
    color: var(--accent-primary);
    font-weight: 700;
}

.light-mode .length-unit {
    color: var(--text-secondary);
}

.light-mode .toggle-label {
    background: var(--bg-secondary);
    border: 2px solid var(--border-color);
}

.light-mode .toggle-input:checked + .toggle-label {
    background: var(--light-accent-bg);
    border-color: var(--accent-primary);
}

.light-mode .toggle-slider {
    background-color: #64748b;
}

.light-mode .toggle-input:checked + .toggle-label .toggle-slider {
    background-color: var(--accent-primary);
}

.light-mode .tip-item {
    color: var(--text-secondary);
}

/* Light mode header and navigation */
.light-mode .header {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
}

.light-mode .nav-link {
    color: var(--text-primary);
}

.light-mode .nav-link:hover,
.light-mode .nav-link.active {
    color: var(--accent-primary);
}

.light-mode .theme-toggle {
    background: rgba(255, 255, 255, 0.9);
    border: 1px solid var(--border-color);
}

.light-mode .theme-toggle:hover {
    background: var(--light-accent-bg);
}

/* Light mode buttons and interactive elements */
.light-mode .cta-button,
.light-mode .support-button,
.light-mode .check-button {
    background: linear-gradient(135deg, var(--accent-primary), var(--accent-secondary));
    color: #ffffff;
    box-shadow: var(--light-shadow);
}

.light-mode .cta-button:hover,
.light-mode .support-button:hover,
.light-mode .check-button:hover {
    background: linear-gradient(135deg, #1d4ed8, #0284c7);
    box-shadow: var(--light-shadow-lg);
}

/* Light mode text elements */
.light-mode .main-heading {
    color: var(--text-primary);
    text-shadow: none;
}

.light-mode .subtitle {
    color: var(--text-secondary);
}

.light-mode .section-title {
    color: var(--text-primary);
}

.light-mode .counter {
    color: var(--accent-primary);
}

.light-mode .counter-label {
    color: var(--text-secondary);
}

/* Responsive Adjustments for Creator Section */
@media (max-width: 768px) {
    .creator-info {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .creator-details {
        text-align: center;
    }

    .creator-name {
        font-size: 2rem;
    }

    .creator-social {
        justify-content: center;
    }

    .security-indicators {
        flex-direction: column;
        align-items: center;
        gap: 1rem;
    }

    .indicator-item {
        min-width: 200px;
        justify-content: center;
    }
}

/* Responsive Design for Security Section */
@media (max-width: 768px) {
    .security-section {
        padding: 2rem 1rem;
        margin-top: 2rem;
        border-radius: 20px;
    }

    .security-content {
        padding: 0 1rem;
        gap: 2rem;
    }

    .security-heading {
        font-size: 2rem;
        margin-bottom: 1.5rem;
    }

    .security-description {
        font-size: 1.1rem;
        margin-bottom: 1.5rem;
        max-width: 100%;
    }

    .threat-stats {
        grid-template-columns: 1fr;
        gap: 1.5rem;
        margin-top: 1.5rem;
    }

    .stat-item {
        padding: 1.5rem;
        min-width: 200px;
        margin: 0 auto;
    }

    .stat-number {
        font-size: 1.8rem;
    }

    .stat-label {
        font-size: 1rem;
        text-align: center;
    }
}

@media (max-width: 480px) {
    .security-section {
        padding: 1.5rem 0.5rem;
        margin-top: 1.5rem;
    }

    .security-content {
        padding: 0 0.5rem;
        gap: 1.5rem;
    }

    .security-heading {
        font-size: 1.8rem;
        margin-bottom: 1rem;
    }

    .security-description {
        font-size: 1rem;
        margin-bottom: 1rem;
        padding: 0 0.5rem;
    }

    .threat-stats {
        gap: 1rem;
        margin-top: 1rem;
    }

    .stat-item {
        padding: 1rem;
        min-width: 180px;
    }

    .stat-number {
        font-size: 1.5rem;
    }

    .stat-label {
        font-size: 0.9rem;
    }
}

/* Page transition classes */
.page-enter {
    opacity: 0;
    transform: translateY(50px);
}

.page-enter-active {
    opacity: 1;
    transform: translateY(0);
    transition: all 0.6s ease-out;
}

.page-exit {
    opacity: 1;
    transform: translateY(0);
}

.page-exit-active {
    opacity: 0;
    transform: translateY(-50px);
    transition: all 0.4s ease-in;
}

/* Responsive Design Breakpoints - Mobile/Tablet Only */
/* Desktop styles remain unchanged in styles.css */

/* Tablet (768px to 991px) */
@media (min-width: 768px) and (max-width: 991px) {
    .header {
        padding: 1rem;
    }

    .header-container {
        flex-direction: column;
        gap: 1rem;
        align-items: center;
    }

    .main-heading {
        font-size: clamp(2.5rem, 5vw, 3.5rem);
        text-align: center;
        line-height: 1.1;
    }

    .subtitle {
        font-size: clamp(1.4rem, 3vw, 1.8rem);
        white-space: normal;
        text-align: center;
        max-width: 90%;
    }

    .content-container {
        max-width: 95%;
        padding: 0 1rem;
    }

    .dynamic-stats {
        grid-template-columns: repeat(2, 1fr);
        gap: 1.5rem;
        margin: 2rem 0;
    }

    .desktop-nav {
        display: block;
        order: 2;
    }

    .nav-container {
        padding: 0.5rem 1rem;
    }

    .nav-list {
        gap: 1rem;
