import React, { useState } from "react";
import { motion, useAnimation } from "framer-motion";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, Bug, Shield, Zap, Palette, Settings, Globe, HelpCircle, CheckCircle2, AlertTriangle, Loader2 } from "lucide-react";
import { bugReportSchema, type BugReport } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";

const severityConfig = {
  critical: {
    icon: AlertTriangle,
    color: "text-red-500",
    bgColor: "bg-red-500/10",
    borderColor: "border-red-500/20",
    label: "Critical",
    description: "System compromised or major security issue"
  },
  high: {
    icon: AlertTriangle,
    color: "text-orange-500",
    bgColor: "bg-orange-500/10",
    borderColor: "border-orange-500/20",
    label: "High",
    description: "Major functionality broken"
  },
  medium: {
    icon: Bug,
    color: "text-yellow-500",
    bgColor: "bg-yellow-500/10",
    borderColor: "border-yellow-500/20",
    label: "Medium",
    description: "Minor issues affecting user experience"
  },
  low: {
    icon: Bug,
    color: "text-green-500",
    bgColor: "bg-green-500/10",
    borderColor: "border-green-500/20",
    label: "Low",
    description: "Cosmetic or minor issues"
  }
};

const categoryConfig = {
  security: { icon: Shield, label: "Security", color: "text-red-500" },
  performance: { icon: Zap, label: "Performance", color: "text-yellow-500" },
  ui: { icon: Palette, label: "User Interface", color: "text-blue-500" },
  functionality: { icon: Settings, label: "Functionality", color: "text-purple-500" },
  compatibility: { icon: Globe, label: "Compatibility", color: "text-green-500" },
  other: { icon: HelpCircle, label: "Other", color: "text-gray-500" }
};

export default function BugReport() {
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const form = useForm<BugReport>({
    resolver: zodResolver(bugReportSchema),
    defaultValues: {
      reporter_name: "",
      reporter_email: "",
      severity: undefined,
      category: undefined,
      summary: "",
      description: "",
      environment: ""
    }
  });

  const submitMutation = useMutation({
    mutationFn: async (data: BugReport) => {
      const response = await apiRequest('POST', '/api/bug-report', data);
      return response;
    },
    onSuccess: () => {
      toast({
        title: "Bug Report Submitted Successfully",
        description: "Thank you for helping us improve! We'll investigate this issue promptly.",
      });
      form.reset();
    },
    onError: (error: any) => {
      console.error('Bug report submission error:', error);
      toast({
        title: "Submission Failed",
        description: "Please check all fields and try again. Make sure your description is at least 10 characters.",
        variant: "destructive",
      });
    }
  });

  const onSubmit = (data: BugReport) => {
    submitMutation.mutate(data);
  };

  const containerVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.8,
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-950 via-blue-950 to-slate-950 relative overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 opacity-30">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-blue-500/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-purple-500/20 rounded-full blur-3xl animate-pulse delay-1000"></div>
      </div>

      {/* Grid Pattern */}
      <div className="absolute inset-0 opacity-50">
        <div className="h-full w-full" style={{
          backgroundImage: `radial-gradient(circle at 1px 1px, rgba(255,255,255,0.15) 1px, transparent 0)`,
          backgroundSize: '20px 20px'
        }}></div>
      </div>

      <div className="relative z-10">
        {/* Header */}
        <motion.header 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="border-b border-white/10 bg-black/20 backdrop-blur-lg"
        >
          <div className="container mx-auto px-6 py-4 flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Link href="/">
                <Button variant="ghost" size="sm" className="text-white/70 hover:text-white hover:bg-white/10" data-testid="button-back-home">
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Back to Home
                </Button>
              </Link>
            </div>
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <Bug className="w-5 h-5 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-white">Bug Report</h1>
                <p className="text-sm text-white/60">Help us improve</p>
              </div>
            </div>
          </div>
        </motion.header>

        {/* Main Content */}
        <div className="container mx-auto px-6 py-12">
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate="visible"
            className="max-w-4xl mx-auto"
          >
            {/* Title Section */}
            <motion.div variants={itemVariants} className="text-center mb-12">
              <h2 className="text-4xl font-bold text-white mb-4">
                Report an Issue
              </h2>
              <p className="text-xl text-white/70 max-w-2xl mx-auto">
                Help us identify and fix bugs to improve the platform for everyone. 
                Your detailed reports make a real difference.
              </p>
            </motion.div>

            {/* Form Card */}
            <motion.div variants={itemVariants}>
              <Card className="bg-white/5 border-white/10 backdrop-blur-lg shadow-2xl">
                <CardHeader className="border-b border-white/10">
                  <CardTitle className="text-2xl text-white flex items-center">
                    <Bug className="w-6 h-6 mr-3 text-blue-400" />
                    Issue Details
                  </CardTitle>
                  <CardDescription className="text-white/60">
                    Please provide as much detail as possible to help us understand and reproduce the issue.
                  </CardDescription>
                </CardHeader>
                <CardContent className="p-8">
                  <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
                      {/* Reporter Info Section */}
                      <motion.div 
                        variants={itemVariants}
                        className="grid grid-cols-1 md:grid-cols-2 gap-6"
                      >
                        <FormField
                          control={form.control}
                          name="reporter_name"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-white">Your Name</FormLabel>
                              <FormControl>
                                <Input 
                                  placeholder="Enter your name"
                                  className="bg-white/5 border-white/20 text-white placeholder:text-white/40 focus:border-blue-400 focus:ring-blue-400/20"
                                  data-testid="input-reporter-name"
                                  {...field}
                                />
                              </FormControl>
                              <FormMessage className="text-red-400" />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name="reporter_email"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-white">Email Address</FormLabel>
                              <FormControl>
                                <Input 
                                  type="email"
                                  placeholder="your@email.com"
                                  className="bg-white/5 border-white/20 text-white placeholder:text-white/40 focus:border-blue-400 focus:ring-blue-400/20"
                                  data-testid="input-reporter-email"
                                  {...field}
                                />
                              </FormControl>
                              <FormMessage className="text-red-400" />
                            </FormItem>
                          )}
                        />
                      </motion.div>

                      {/* Severity and Category */}
                      <motion.div 
                        variants={itemVariants}
                        className="grid grid-cols-1 md:grid-cols-2 gap-6"
                      >
                        <FormField
                          control={form.control}
                          name="severity"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-white">Severity Level</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger 
                                    className="bg-white/5 border-white/20 text-white focus:border-blue-400 focus:ring-blue-400/20"
                                    data-testid="select-severity"
                                  >
                                    <SelectValue placeholder="Select severity" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent className="bg-slate-900 border-white/20">
                                  {Object.entries(severityConfig).map(([key, config]) => {
                                    const Icon = config.icon;
                                    return (
                                      <SelectItem key={key} value={key} className="text-white hover:bg-white/10">
                                        <div className="flex items-center space-x-3">
                                          <Icon className={`w-4 h-4 ${config.color}`} />
                                          <div>
                                            <div className="font-medium">{config.label}</div>
                                            <div className="text-xs text-white/60">{config.description}</div>
                                          </div>
                                        </div>
                                      </SelectItem>
                                    );
                                  })}
                                </SelectContent>
                              </Select>
                              <FormMessage className="text-red-400" />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name="category"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-white">Category</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger 
                                    className="bg-white/5 border-white/20 text-white focus:border-blue-400 focus:ring-blue-400/20"
                                    data-testid="select-category"
                                  >
                                    <SelectValue placeholder="Select category" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent className="bg-slate-900 border-white/20">
                                  {Object.entries(categoryConfig).map(([key, config]) => {
                                    const Icon = config.icon;
                                    return (
                                      <SelectItem key={key} value={key} className="text-white hover:bg-white/10">
                                        <div className="flex items-center space-x-2">
                                          <Icon className={`w-4 h-4 ${config.color}`} />
                                          <span>{config.label}</span>
                                        </div>
                                      </SelectItem>
                                    );
                                  })}
                                </SelectContent>
                              </Select>
                              <FormMessage className="text-red-400" />
                            </FormItem>
                          )}
                        />
                      </motion.div>

                      {/* Summary */}
                      <motion.div variants={itemVariants}>
                        <FormField
                          control={form.control}
                          name="summary"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-white">Issue Summary</FormLabel>
                              <FormControl>
                                <Input 
                                  placeholder="Brief description of the issue"
                                  className="bg-white/5 border-white/20 text-white placeholder:text-white/40 focus:border-blue-400 focus:ring-blue-400/20"
                                  data-testid="input-summary"
                                  {...field}
                                />
                              </FormControl>
                              <FormMessage className="text-red-400" />
                            </FormItem>
                          )}
                        />
                      </motion.div>

                      {/* Description */}
                      <motion.div variants={itemVariants}>
                        <FormField
                          control={form.control}
                          name="description"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-white">Detailed Description</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="Please provide detailed information:&#10;• What were you trying to do?&#10;• What happened instead?&#10;• Steps to reproduce the issue&#10;• Expected vs actual behavior&#10;• Any error messages you saw"
                                  className="bg-white/5 border-white/20 text-white placeholder:text-white/40 focus:border-blue-400 focus:ring-blue-400/20 min-h-[120px] resize-none"
                                  data-testid="textarea-description"
                                  {...field}
                                />
                              </FormControl>
                              <FormMessage className="text-red-400" />
                            </FormItem>
                          )}
                        />
                      </motion.div>

                      {/* Environment */}
                      <motion.div variants={itemVariants}>
                        <FormField
                          control={form.control}
                          name="environment"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="text-white">Environment Information (Optional)</FormLabel>
                              <FormControl>
                                <Textarea 
                                  placeholder="Browser, operating system, device info, etc."
                                  className="bg-white/5 border-white/20 text-white placeholder:text-white/40 focus:border-blue-400 focus:ring-blue-400/20 min-h-[80px] resize-none"
                                  data-testid="textarea-environment"
                                  {...field}
                                />
                              </FormControl>
                              <FormMessage className="text-red-400" />
                            </FormItem>
                          )}
                        />
                      </motion.div>

                      {/* Submit Button */}
                      <motion.div variants={itemVariants} className="flex justify-end pt-6">
                        <Button 
                          type="submit" 
                          disabled={submitMutation.isPending}
                          className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white px-8 py-3 font-semibold shadow-lg transition-all duration-200 hover:shadow-xl hover:scale-105"
                          data-testid="button-submit-bug-report"
                        >
                          {submitMutation.isPending ? (
                            <>
                              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                              Submitting...
                            </>
                          ) : (
                            <>
                              <CheckCircle2 className="w-4 h-4 mr-2" />
                              Submit Bug Report
                            </>
                          )}
                        </Button>
                      </motion.div>
                    </form>
                  </Form>
                </CardContent>
              </Card>
            </motion.div>

            {/* Tips Section */}
            <motion.div variants={itemVariants} className="mt-12">
              <Card className="bg-gradient-to-r from-blue-900/20 to-purple-900/20 border-blue-500/20 backdrop-blur-lg">
                <CardHeader>
                  <CardTitle className="text-white flex items-center">
                    <Shield className="w-5 h-5 mr-2 text-blue-400" />
                    Reporting Tips
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-white/70">
                    <div className="flex items-start space-x-3">
                      <div className="w-2 h-2 bg-blue-400 rounded-full mt-2 flex-shrink-0"></div>
                      <p>Be specific about what you were doing when the bug occurred</p>
                    </div>
                    <div className="flex items-start space-x-3">
                      <div className="w-2 h-2 bg-purple-400 rounded-full mt-2 flex-shrink-0"></div>
                      <p>Include steps to reproduce the issue if possible</p>
                    </div>
                    <div className="flex items-start space-x-3">
                      <div className="w-2 h-2 bg-green-400 rounded-full mt-2 flex-shrink-0"></div>
                      <p>Screenshots or error messages help us debug faster</p>
                    </div>
                    <div className="flex items-start space-x-3">
                      <div className="w-2 h-2 bg-yellow-400 rounded-full mt-2 flex-shrink-0"></div>
                      <p>Critical security issues are prioritized immediately</p>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </motion.div>
          </motion.div>
        </div>
      </div>
    </div>
  );
}

